import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const ProductEdit = () => {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    name: '',
    price: 0,
    description: '',
    image: '',
    stock: 0,
    isFeatured: false
  });
  const [loading, setLoading] = useState({
    fetching: false,
    updating: false
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  // Verify API endpoint is reachable (unchanged)
  const verifyBackendConnection = async () => {
    try {
      await axios.get(`${process.env.REACT_APP_API_URL}/api/health`);
      return true;
    } catch (err) {
      console.error('Backend connection failed:', err);
      setError('Cannot connect to server. Please try again later.');
      return false;
    }
  };

  // Error handler (unchanged)
  const handleApiError = useCallback((err, action) => {
    console.error(`Error ${action} product:`, err);
    
    if (err.response) {
      switch (err.response.status) {
        case 401:
          setError('Session expired. Please login again.');
          navigate('/login');
          break;
        case 404:
          setError('Product not found. It may have been deleted.');
          navigate('/admin/products');
          break;
        case 500:
          setError('Server error. Please try again later.');
          break;
        default:
          setError(err.response.data?.message || 'An error occurred');
      }
    } else if (err.request) {
      setError('No response from server. Check your connection.');
    } else {
      setError(err.message || 'An unexpected error occurred');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProduct = async () => {
      if (!userInfo?.isAdmin) {
        navigate('/login');
        return;
      }

      if (!(await verifyBackendConnection())) return;

      try {
        setLoading(prev => ({ ...prev, fetching: true }));
        setError(null);
        
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/products/${id}`,
          {
            headers: { 
              Authorization: `Bearer ${userInfo.token}`,
              'Content-Type': 'application/json' // Added missing header
            },
            timeout: 5000
          }
        );

        if (!data) {
          throw new Error('Product data not received');
        }

        setFormData({
          name: data.name || '',
          price: data.price || 0,
          description: data.description || '',
          image: data.image || '',
          stock: data.stock || data.countInStock || 0,
          isFeatured: data.isFeatured || false
        });
      } catch (err) {
        handleApiError(err, 'fetch');
      } finally {
        setLoading(prev => ({ ...prev, fetching: false }));
      }
    };

    fetchProduct();
  }, [id, userInfo, navigate, handleApiError]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!(await verifyBackendConnection())) return;

    try {
      setLoading(prev => ({ ...prev, updating: true }));
      setError(null);
      
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/products/${id}`,
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`
          },
          timeout: 5000
        }
      );

      setSuccess('Product updated successfully!');
      setTimeout(() => navigate('/admin/products'), 2000);
    } catch (err) {
      handleApiError(err, 'updating');
    } finally {
      setLoading(prev => ({ ...prev, updating: false }));
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : type === 'number' ? Number(value) : value
    }));
  };

  if (loading.fetching) {
    return (
      <div className="container my-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p>Loading product details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger">
          <i className="bi bi-exclamation-triangle-fill me-2"></i>
          {error}
          <button 
            className="btn btn-sm btn-outline-danger ms-3"
            onClick={() => window.location.reload()}
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <div className="card shadow">
        <div className="card-body">
          <h2 className="card-title mb-4">Edit Product</h2>
          
          {success && (
            <div className="alert alert-success">
              <i className="bi bi-check-circle-fill me-2"></i>
              {success}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            {/* All your original form fields remain exactly the same */}
            {/* ... */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductEdit;
