import React, { useEffect, useState, useContext } from 'react';
import Product from './Product';
import { AppContext } from '../context/AppContext';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { state } = useContext(AppContext);

  useEffect(() => {
    let isMounted = true;

    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/products/featured`,
          {
            headers: {
              'Content-Type': 'application/json',
              Authorization: state.userInfo ? `Bearer ${state.userInfo.token}` : '',
            },
          }
        );
        
        const data = await response.json();
        
        if (isMounted) {
          if (response.ok) {
            // Ensure countInStock exists for each product
            const normalizedProducts = Array.isArray(data) 
              ? data.map(p => ({
                  ...p,
                  countInStock: p.countInStock ?? p.stock ?? 0
                }))
              : [];
            setProducts(normalizedProducts);
          } else {
            setError(data.message || 'Failed to fetch products');
          }
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setProducts([]);
          setLoading(false);
        }
      }
    };

    fetchProducts();

    return () => { isMounted = false };
  }, [state.userInfo]);

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container my-4">
      <h2 className="mb-4">Featured Products</h2>
      <div className="row">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product._id} className="col-md-4 col-lg-3 mb-4">
              <Product product={product} />
            </div>
          ))
        ) : (
          <div className="col-12">
            <p>No featured products available</p>
          </div>
        )}
      </div>
     <style>{`
  /* Featured Products Grid Container */
  .row {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 25px;
    margin: 2rem 0;
    padding: 0 1rem;
  }

  /* Individual Product Card Container */
  .col-md-4.col-lg-3.mb-4 {
    flex: 0 0 calc(25% - 25px);
    max-width: calc(25% - 25px);
    min-width: 250px;
    transition: all 0.3s ease;
  }

  /* No Products Available Message */
  .col-12 {
    width: 100%;
    text-align: center;
  }

  .col-12 p {
    color: #666;
    font-size: 1.1rem;
    background: #f9f9f9;
    padding: 2rem;
    border-radius: 12px;
    border: 1px dashed #22c9be;
    margin: 2rem auto;
    max-width: 500px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.8rem;
    box-shadow: 0 2px 10px rgba(34, 201, 190, 0.1);
  }

  .col-12 p::before {
    content: "✖";
    color: #22c9be;
    font-size: 1.3rem;
  }

  /* Responsive Adjustments */
  @media (max-width: 992px) {
    .col-md-4.col-lg-3.mb-4 {
      flex: 0 0 calc(33.333% - 25px);
      max-width: calc(33.333% - 25px);
    }
  }

  @media (max-width: 768px) {
    .col-md-4.col-lg-3.mb-4 {
      flex: 0 0 calc(50% - 25px);
      max-width: calc(50% - 25px);
    }
  }

  @media (max-width: 576px) {
    .row {
      gap: 15px;
    }
    
    .col-md-4.col-lg-3.mb-4 {
      flex: 0 0 100%;
      max-width: 100%;
      min-width: auto;
    }

    .col-12 p {
      padding: 1.5rem;
      font-size: 1rem;
    }
  }
`}</style>
    </div>
  );
};

export default ProductList;
