import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppContext } from '../context/AppContext';

const Header = () => {
  const { state, logout } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();
  const [showDropdown, setShowDropdown] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
    setShowDropdown(false);
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <header className="bg-dark text-white p-3">
      <div className="container">
        <div className="d-flex justify-content-between align-items-center">
          <Link to="/" className="text-white text-decoration-none">
            <h1>ShopEasy</h1>
          </Link>
          
          <div className="d-flex align-items-center gap-3">
            <Link to="/cart" className="text-white text-decoration-none me-3">
              <i className="bi bi-cart-fill"></i> Cart
              {state.cart.cartItems.length > 0 && (
                <span className="badge bg-danger ms-1">
                  {state.cart.cartItems.reduce((a, c) => a + c.qty, 0)}
                </span>
              )}
            </Link>
            
            {userInfo ? (
              <div className="position-relative">
                <button
                  className="btn btn-secondary d-flex align-items-center"
                  onClick={toggleDropdown}
                  aria-expanded={showDropdown}
                >
                  <i className="bi bi-person-fill me-2"></i>
                  {userInfo.name}
                  <i className={`bi bi-chevron-${showDropdown ? 'up' : 'down'} ms-2`}></i>
                </button>
                
                {showDropdown && (
                  <div className="dropdown-menu show position-absolute end-0 mt-2" style={{ zIndex: 1000 }}>
                    <Link 
                      to="/profile" 
                      className="dropdown-item"
                      onClick={() => setShowDropdown(false)}
                    >
                      <i className="bi bi-person-circle me-2"></i> Profile
                    </Link>
                    <Link 
                      to="/order-history" 
                      className="dropdown-item"
                      onClick={() => setShowDropdown(false)}
                    >
                      <i className="bi bi-receipt me-2"></i> My Orders
                    </Link>
                    {userInfo.isAdmin && (
                      <Link 
                        to="/admin/dashboard" 
                        className="dropdown-item"
                        onClick={() => setShowDropdown(false)}
                      >
                        <i className="bi bi-speedometer2 me-2"></i> Admin Panel
                      </Link>
                    )}
                    <div className="dropdown-divider"></div>
                    <button 
                      className="dropdown-item text-danger"
                      onClick={handleLogout}
                    >
                      <i className="bi bi-box-arrow-right me-2"></i> Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <Link to="/login" className="text-white text-decoration-none">
                <i className="bi bi-box-arrow-in-right me-1"></i> Login
              </Link>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
