import React, { useEffect, useState, useContext } from 'react';
import Product from './Product';
import { AppContext } from '../context/AppContext';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { state } = useContext(AppContext);

  useEffect(() => {
    let isMounted = true;

    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/products/featured`,
          {
            headers: {
              'Content-Type': 'application/json',
              Authorization: state.userInfo ? `Bearer ${state.userInfo.token}` : '',
            },
          }
        );
        
        const data = await response.json();
        
        if (isMounted) {
          if (response.ok) {
            // Ensure countInStock exists for each product
            const normalizedProducts = Array.isArray(data) 
              ? data.map(p => ({
                  ...p,
                  countInStock: p.countInStock ?? p.stock ?? 0
                }))
              : [];
            setProducts(normalizedProducts);
          } else {
            setError(data.message || 'Failed to fetch products');
          }
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setProducts([]);
          setLoading(false);
        }
      }
    };

    fetchProducts();

    return () => { isMounted = false };
  }, [state.userInfo]);

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container my-4">
      <h2 className="mb-4">Featured Products</h2>
      <div className="row">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product._id} className="col-md-4 col-lg-3 mb-4">
              <Product product={product} />
            </div>
          ))
        ) : (
          <div className="col-12">
            <p>No featured products available</p>
          </div>
        )}
      </div>
     <style>{`
  /* === Featured Products Section === */
  .featured-products-section {
    padding: 5rem 2rem;
    background: linear-gradient(to bottom, #f8fafc 0%, #ffffff 100%);
    position: relative;
    overflow: hidden;
    animation: fadeIn 0.8s cubic-bezier(0.22, 1, 0.36, 1) forwards;
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(30px); }
    to { opacity: 1; transform: translateY(0); }
  }

  /* === Section Header === */
  .section-header {
    text-align: center;
    margin-bottom: 4rem;
    max-width: 1200px;
    margin-left: auto;
    margin-right: auto;
  }

  .section-title {
    font-size: 2.5rem;
    font-weight: 700;
    color: #1a3a4a;
    margin-bottom: 1.5rem;
    position: relative;
    display: inline-block;
    opacity: 0;
    animation: slideInFromTop 0.8s 0.3s cubic-bezier(0.22, 1, 0.36, 1) forwards;
  }

  @keyframes slideInFromTop {
    from { opacity: 0; transform: translateY(-30px); }
    to { opacity: 1; transform: translateY(0); }
  }

  .section-title::after {
    content: '';
    position: absolute;
    bottom: -12px;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 4px;
    background: linear-gradient(90deg, #22c9be, #1aa89f);
    border-radius: 2px;
    box-shadow: 0 4px 12px rgba(34, 201, 190, 0.3);
  }

  /* === Product Grid Container === */
  .product-grid-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 2rem;
  }

  /* === Product Grid === */
  .product-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 2.5rem;
    perspective: 1200px;
  }

  /* === Product Card Wrapper === */
  .product-card-wrapper {
    position: relative;
    transition: all 0.5s cubic-bezier(0.16, 1, 0.3, 1);
    transform-style: preserve-3d;
    animation: cardEntrance 0.8s cubic-bezier(0.22, 1, 0.36, 1) forwards;
    opacity: 0;
  }

  @keyframes cardEntrance {
    from {
      opacity: 0;
      transform: translateY(40px) rotateX(-20deg);
    }
    to {
      opacity: 1;
      transform: translateY(0) rotateX(0);
    }
  }

  /* Staggered animations */
  .product-card-wrapper:nth-child(1) { animation-delay: 0.1s; }
  .product-card-wrapper:nth-child(2) { animation-delay: 0.2s; }
  .product-card-wrapper:nth-child(3) { animation-delay: 0.3s; }
  .product-card-wrapper:nth-child(4) { animation-delay: 0.4s; }
  .product-card-wrapper:nth-child(5) { animation-delay: 0.5s; }
  .product-card-wrapper:nth-child(6) { animation-delay: 0.6s; }

  .product-card-wrapper:hover {
    transform: translateY(-10px) scale(1.03);
    z-index: 10;
  }

  /* === Loading State === */
  .loading-state {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 400px;
    position: relative;
  }

  .loading-spinner {
    width: 70px;
    height: 70px;
    border: 5px solid rgba(34, 201, 190, 0.2);
    border-top-color: #22c9be;
    border-radius: 50%;
    animation: spin 1.5s linear infinite;
    margin-bottom: 2rem;
  }

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  .loading-text {
    color: #555;
    font-size: 1.2rem;
    font-weight: 500;
    animation: pulse 2s ease-in-out infinite;
  }

  @keyframes pulse {
    0%, 100% { opacity: 0.8; }
    50% { opacity: 1; }
  }

  /* === Error State === */
  .error-state {
    background-color: rgba(220, 53, 69, 0.05);
    border-left: 4px solid #dc3545;
    color: #dc3545;
    border-radius: 12px;
    padding: 2rem;
    margin: 3rem auto;
    max-width: 700px;
    text-align: center;
    animation: gentleShake 0.8s cubic-bezier(0.36, 0.07, 0.19, 0.97) both;
  }

  @keyframes gentleShake {
    0%, 100% { transform: translateX(0); }
    15%, 45%, 75% { transform: translateX(-5px); }
    30%, 60%, 90% { transform: translateX(5px); }
  }

  .error-icon {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    animation: pulse 2s infinite;
  }

  .retry-button {
    background: linear-gradient(135deg, #22c9be, #1aa89f);
    border: none;
    color: white;
    padding: 0.75rem 2rem;
    border-radius: 50px;
    font-weight: 600;
    margin-top: 2rem;
    transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
    box-shadow: 0 4px 15px rgba(34, 201, 190, 0.3);
  }

  .retry-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(34, 201, 190, 0.4);
  }

  /* === Empty State === */
  .empty-state {
    text-align: center;
    padding: 4rem 2rem;
    background-color: rgba(34, 201, 190, 0.03);
    border-radius: 16px;
    border: 1px dashed rgba(34, 201, 190, 0.3);
    max-width: 700px;
    margin: 2rem auto;
    animation: fadeInWithPulse 2.5s ease-in-out infinite alternate;
  }

  @keyframes fadeInWithPulse {
    0% {
      opacity: 0.9;
      transform: scale(0.98);
      box-shadow: 0 0 0 rgba(34, 201, 190, 0);
    }
    100% {
      opacity: 1;
      transform: scale(1);
      box-shadow: 0 0 0 15px rgba(34, 201, 190, 0.05);
    }
  }

  .empty-icon {
    font-size: 3.5rem;
    color: #22c9be;
    margin-bottom: 1.5rem;
    opacity: 0.8;
  }

  .empty-text {
    color: #6b7280;
    font-size: 1.3rem;
    margin-bottom: 2rem;
  }

  .shop-button {
    background: linear-gradient(135deg, #22c9be, #1aa89f);
    border: none;
    color: white;
    padding: 0.75rem 2rem;
    border-radius: 50px;
    font-weight: 600;
    transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
    box-shadow: 0 4px 15px rgba(34, 201, 190, 0.3);
  }

  .shop-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(34, 201, 190, 0.4);
  }

  /* === Responsive Adjustments === */
  @media (max-width: 1400px) {
    .product-grid {
      grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    }
  }

  @media (max-width: 1200px) {
    .featured-products-section {
      padding: 4rem 1.5rem;
    }
    
    .section-title {
      font-size: 2.2rem;
    }
  }

  @media (max-width: 992px) {
    .product-grid {
      gap: 2rem;
    }
    
    .section-title {
      font-size: 2rem;
      margin-bottom: 1.25rem;
    }
    
    .section-title::after {
      width: 80px;
      height: 3px;
      bottom: -10px;
    }
  }

  @media (max-width: 768px) {
    .featured-products-section {
      padding: 3rem 1rem;
    }
    
    .product-grid {
      grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
      gap: 1.5rem;
    }
    
    .section-title {
      font-size: 1.8rem;
    }
  }

  @media (max-width: 576px) {
    .product-grid {
      grid-template-columns: 1fr;
      max-width: 400px;
      margin: 0 auto;
    }
    
    .section-title {
      font-size: 1.6rem;
    }
    
    .section-header {
      margin-bottom: 3rem;
    }
  }

  /* === Accessibility Focus === */
  .product-card-wrapper:focus-within {
    outline: 2px solid #1aa89f;
    outline-offset: 5px;
    border-radius: 12px;
    transform: translateY(-5px) scale(1.02);
  }
`}</style>
    </div>
  );
};

export default ProductList;
