import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';
import { Card, Button, Badge, Spinner, Alert, Form } from 'react-bootstrap';
import Rating from './Rating';

const Product = ({ product }) => {
  const { state, addToCart } = useContext(AppContext);
  const { userInfo } = state;
  const [qty, setQty] = useState(1);
  const [imgError, setImgError] = useState(false);
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [cartError, setCartError] = useState(null);
  const [isHovered, setIsHovered] = useState(false);

  // Set default quantity to 1 when product changes
  useEffect(() => {
    setQty(1);
  }, [product]);

  const fallbackImage = '/images/product-placeholder.jpg';

  const handleAddToCart = async () => {
    if (!userInfo) {
      setCartError('Please login to add items to cart');
      return;
    }

    try {
      setIsAddingToCart(true);
      setCartError(null);
      await addToCart(product, Number(qty));
    } catch (err) {
      setCartError(err.response?.data?.message || 'Failed to add to cart');
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleImageError = (e) => {
    setImgError(true);
    e.target.src = fallbackImage;
  };

  const handleQtyChange = (e) => {
    const value = Math.max(1, Math.min(product.countInStock, Number(e.target.value) || 1));
    setQty(value);
  };

  return (
    <Card 
      className={`h-100 shadow-sm product-card ${isHovered ? 'shadow' : ''}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <Link to={`/product/${product._id}`} className="text-decoration-none">
        <div className="text-center p-3" style={{ height: '200px', backgroundColor: '#f8f9fa' }}>
          <Card.Img
            variant="top"
            src={imgError ? fallbackImage : product.image}
            style={{ 
              maxHeight: '100%', 
              width: 'auto', 
              objectFit: 'contain',
              transition: 'transform 0.3s ease',
              transform: isHovered ? 'scale(1.05)' : 'scale(1)'
            }}
            onError={handleImageError}
            alt={product.name}
            loading="lazy"
          />
        </div>
      </Link>

      <Card.Body className="d-flex flex-column">
        <Link to={`/product/${product._id}`} className="text-decoration-none">
          <Card.Title className="mb-2 fs-6 text-dark">{product.name}</Card.Title>
        </Link>
        
        <Rating 
          value={product.rating} 
          text={`${product.numReviews} reviews`} 
          className="mb-2"
        />

        <Card.Text className="text-muted small mb-2" style={{ flexGrow: 1 }}>
          {product.description?.length > 100 
            ? `${product.description.substring(0, 100)}...` 
            : product.description || 'No description available'}
        </Card.Text>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0 text-primary">
            ${product.price.toFixed(2)}
            {product.countInStock <= 10 && product.countInStock > 0 && (
              <span className="text-danger small ms-2">
                (Only {product.countInStock} left!)
              </span>
            )}
          </h5>
          
          {product.countInStock > 0 ? (
            <Badge bg="success" pill>In Stock</Badge>
          ) : (
            <Badge bg="secondary" pill>Out of Stock</Badge>
          )}
        </div>
        
        {cartError && (
          <Alert variant="danger" className="py-1 mb-2" dismissible onClose={() => setCartError(null)}>
            {cartError}
          </Alert>
        )}

        {product.countInStock > 0 && (
          <div className="mt-auto">
            <div className="d-flex align-items-center mb-2">
              <Form.Control
                type="number"
                min="1"
                max={product.countInStock}
                value={qty}
                onChange={handleQtyChange}
                style={{ width: '70px' }}
                aria-label="Quantity"
              />
              <Button
                variant="primary"
                onClick={handleAddToCart}
                disabled={isAddingToCart || !userInfo}
                className="ms-2 flex-grow-1"
              >
                {isAddingToCart ? (
                  <>
                    <Spinner
                      as="span"
                      animation="border"
                      size="sm"
                      role="status"
                      aria-hidden="true"
                      className="me-2"
                    />
                    Adding...
                  </>
                ) : (
                  userInfo ? 'Add to Cart' : 'Login to Buy'
                )}
              </Button>
            </div>
          </div>
        )}
        
        {userInfo?.isAdmin && (
          <Link
            to={`/admin/product/${product._id}/edit`}
            className="btn btn-outline-secondary btn-sm mt-2"
          >
            <i className="bi bi-pencil-square me-1"></i> Edit Product
          </Link>
        )}
      </Card.Body>
    </Card>
  );
};

export default Product;
