import React, { useEffect, useState, useContext } from 'react';
import Product from './Product';
import { AppContext } from '../context/AppContext';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { state } = useContext(AppContext);

  useEffect(() => {
    let isMounted = true;

    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `${process.env.REACT_APP_API_URL}/api/products/featured`,
          {
            headers: {
              'Content-Type': 'application/json',
              Authorization: state.userInfo ? `Bearer ${state.userInfo.token}` : '',
            },
          }
        );
        
        const data = await response.json();
        
        if (isMounted) {
          if (response.ok) {
            // Ensure countInStock exists for each product
            const normalizedProducts = Array.isArray(data) 
              ? data.map(p => ({
                  ...p,
                  countInStock: p.countInStock ?? p.stock ?? 0
                }))
              : [];
            setProducts(normalizedProducts);
          } else {
            setError(data.message || 'Failed to fetch products');
          }
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setProducts([]);
          setLoading(false);
        }
      }
    };

    fetchProducts();

    return () => { isMounted = false };
  }, [state.userInfo]);

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container my-4">
      <h2 className="mb-4">Featured Products</h2>
      <div className="row">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product._id} className="col-md-4 col-lg-3 mb-4">
              <Product product={product} />
            </div>
          ))
        ) : (
          <div className="col-12">
            <p>No featured products available</p>
          </div>
        )}
      </div>
     <style>{`
  /* Product List Container */
  .container.my-4 {
    max-width: 1200px;
    padding: 0 20px;
    margin: 0 auto;
  }

  /* Title Styling */
  h2.mb-4 {
    color: #22c9be;
    font-weight: 700;
    font-size: 1.8rem;
    position: relative;
    padding-bottom: 10px;
    letter-spacing: 0.5px;
  }

  h2.mb-4::after {
    content: '';
    display: block;
    width: 60px;
    height: 3px;
    background: linear-gradient(90deg, #22c9be, #1aa89f);
    position: absolute;
    bottom: 0;
    left: 0;
    border-radius: 3px;
  }

  /* Loading State */
  .text-center.my-5 {
    color: #555;
    font-size: 1.2rem;
    padding: 40px 0;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px;
  }

  /* Error State */
  .alert.alert-danger {
    background: #ffebee;
    color: #d32f2f;
    padding: 15px 20px;
    border-radius: 6px;
    margin: 20px 0;
    border-left: 4px solid #d32f2f;
    animation: fadeIn 0.3s ease-out;
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(-10px); }
    to { opacity: 1; transform: translateY(0); }
  }

  /* Product Grid */
  .row {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 25px;
    margin-top: 30px;
  }

  .col-md-4.col-lg-3.mb-4 {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .col-md-4.col-lg-3.mb-4:hover {
    transform: translateY(-5px);
  }

  /* Empty State */
  .col-12 p {
    color: #666;
    font-size: 1rem;
    text-align: center;
    padding: 40px 0;
    background: #f9f9f9;
    border-radius: 8px;
  }

  /* Responsive Adjustments */
  @media (max-width: 768px) {
    .row {
      grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
      gap: 20px;
    }

    h2.mb-4 {
      font-size: 1.5rem;
    }
  }

  @media (max-width: 576px) {
    .row {
      grid-template-columns: 1fr;
      gap: 15px;
    }

    .container.my-4 {
      padding: 0 15px;
    }
  }
`}</style>
    </div>
  );
};

export default ProductList;
