import React, { useState, useContext } from 'react';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const { login } = useContext(AppContext);
  const navigate = useNavigate();
  const location = useLocation();

  const redirect = location.search ? location.search.split('=')[1] : '/';

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      await login(email, password);
      navigate(redirect || '/');
    } catch (err) {
      // Properly extract error message from error object
      const errorMessage = err.message || err.toString() || 'Login failed. Please try again.';
      setError(errorMessage); // Store just the message, not the whole error object
    }
  };

  return (
    <div className="container my-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <h2 className="card-title text-center mb-4">Sign In</h2>
              {/* Render error message safely */}
              {error && (
                <div className="alert alert-danger" role="alert">
                  {error} {/* Now rendering a string, not an Error object */}
                </div>
              )}
              <form onSubmit={submitHandler}>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    Email Address
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    autoComplete="email"
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="password" className="form-label">
                    Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    autoComplete="current-password"
                  />
                </div>
                <button type="submit" className="btn btn-primary w-100 py-2">
                  Sign In
                </button>
              </form>
              <div className="mt-3 text-center">
                <small>
                  New Customer?{' '}
                  <Link 
                    to={redirect ? `/register?redirect=${redirect}` : '/register'}
                    className="text-decoration-none"
                  >
                    Create an account
                  </Link>
                </small>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
