import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate, Navigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const AdminPanel = () => {
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect if not admin
    if (!userInfo || !userInfo.isAdmin) {
      navigate('/admin/login', { replace: true });
      return;
    }

    const controller = new AbortController();
    
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/admin/products`,
          {
            headers: { Authorization: `Bearer ${userInfo.token}` },
            signal: controller.signal,
            timeout: 8000
          }
        );

        const productsData = Array.isArray(data?.products) ? data.products : [];
        setProducts(productsData.map(p => ({ ...p, isFeatured: true })));
      } catch (err) {
        if (!axios.isCancel(err)) {
          console.error('Fetch error:', err);
          setError(err.response?.data?.message || err.message || 'Failed to load products');
        }
      } finally {
        if (!controller.signal.aborted) {
          setLoading(false);
        }
      }
    };

    fetchProducts();

    return () => controller.abort();
  }, [userInfo, navigate]);

  const handleProductAction = async (action, id, data = null) => {
    try {
      const config = {
        headers: { Authorization: `Bearer ${userInfo.token}` },
        timeout: 8000
      };

      switch (action) {
        case 'delete':
          if (!window.confirm('Delete this product permanently?')) return;
          await axios.delete(
            `${process.env.REACT_APP_API_URL}/api/admin/products/${id}`,
            config
          );
          setProducts(prev => prev.filter(p => p._id !== id));
          break;

        case 'feature':
          await axios.put(
            `${process.env.REACT_APP_API_URL}/api/admin/products/${id}`,
            { ...data, isFeatured: true },
            config
          );
          setProducts(prev => prev.map(p => 
            p._id === id ? { ...p, isFeatured: true } : p
          ));
          break;

        default:
          throw new Error('Invalid action');
      }
    } catch (err) {
      console.error(`${action} error:`, err);
      const message = err.response?.data?.message || 
                    `Failed to ${action} product`;
      alert(`Error: ${message}`);
    }
  };

  const markAllFeatured = async () => {
    if (!window.confirm('Mark ALL products as featured?')) return;
    
    try {
      await Promise.all(
        products.map(product => 
          handleProductAction('feature', product._id, product)
        )
      );
      alert('All products marked as featured!');
    } catch (err) {
      console.error('Bulk feature error:', err);
    }
  };

  // Render redirect after hooks
  if (!userInfo || !userInfo.isAdmin) {
    return <Navigate to="/admin/login" replace />;
  }

  if (error) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger">
          <h3>Error Loading Products</h3>
          <p>{error}</p>
          <button 
            className="btn btn-primary mt-2"
            onClick={() => window.location.reload()}
          >
            Refresh Page
          </button>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="d-flex justify-content-center my-5 py-5">
        <div className="spinner-border text-primary" style={{width: '3rem', height: '3rem'}}>
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container my-4">
      <div className="d-flex flex-wrap justify-content-between align-items-center mb-4 gap-3">
        <h2 className="mb-0">Admin Product Dashboard</h2>
        <div className="d-flex flex-wrap gap-2">
          <button 
            className="btn btn-success"
            onClick={markAllFeatured}
            disabled={!products.length}
          >
            Mark All Featured
          </button>
          <Link 
            to="/admin/products/create" 
            className="btn btn-primary"
          >
            Create Product
          </Link>
        </div>
      </div>

      {!products.length ? (
        <div className="alert alert-info">
          <div className="d-flex justify-content-between align-items-center">
            <span>No products found</span>
            <Link to="/admin/products/create" className="btn btn-sm btn-info">
              Create First Product
            </Link>
          </div>
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-hover align-middle">
            <thead className="table-light">
              <tr>
                <th>Product</th>
                <th className="text-end">Price</th>
                <th>Stock</th>
                <th>Status</th>
                <th className="text-end">Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product._id}>
                  <td>
                    <div className="d-flex align-items-center gap-3">
                      <img 
                        src={product.image} 
                        alt={product.name}
                        className="rounded"
                        style={{width: '60px', height: '60px', objectFit: 'cover'}}
                        onError={(e) => {
                          e.target.src = '/images/placeholder-product.png';
                        }}
                      />
                      <div>
                        <strong>{product.name}</strong>
                        <div className="text-muted small">ID: {product._id.substring(0, 8)}...</div>
                      </div>
                    </div>
                  </td>
                  <td className="text-end">${product.price.toFixed(2)}</td>
                  <td>
                    <span className={product.stock <= 0 ? 'text-danger fw-bold' : ''}>
                      {product.stock}
                    </span>
                  </td>
                  <td>
                    <span className="badge bg-success">Featured</span>
                  </td>
                  <td className="text-end">
                    <div className="d-flex gap-2 justify-content-end">
                      <Link
                        to={`/admin/products/${product._id}/edit`}
                        className="btn btn-sm btn-outline-primary"
                      >
                        Edit
                      </Link>
                      <button
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleProductAction('delete', product._id)}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
