import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';
import { Card, Button, Badge } from 'react-bootstrap';

const Product = ({ product }) => {
  const { state, addToCart } = useContext(AppContext);
  const { userInfo } = state;
  const [qty, setQty] = useState(1);

  const handleAddToCart = () => {
    addToCart(product, Number(qty));
  };

  return (
    <Card className="h-100 shadow-sm">
      <div className="text-center p-3" style={{ height: '200px' }}>
        <Card.Img
          variant="top"
          src={product.image}
          style={{ maxHeight: '100%', width: 'auto', objectFit: 'contain' }}
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
          }}
        />
      </div>
      <Card.Body className="d-flex flex-column">
        <Card.Title className="mb-2">{product.name}</Card.Title>
        <Card.Text className="text-muted small mb-2" style={{ flexGrow: 1 }}>
          {product.description.length > 100 
            ? `${product.description.substring(0, 100)}...` 
            : product.description}
        </Card.Text>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0 text-primary">${product.price.toFixed(2)}</h5>
          {product.countInStock > 0 ? (
            <Badge bg="success">In Stock</Badge>
          ) : (
            <Badge bg="danger">Out of Stock</Badge>
          )}
        </div>
        
        {userInfo && product.countInStock > 0 && (
          <div className="mt-auto">
            <div className="input-group mb-3">
              <input
                type="number"
                min="1"
                max={product.countInStock}
                value={qty}
                onChange={(e) => setQty(Math.max(1, Math.min(product.countInStock, e.target.value)))}
                className="form-control"
              />
              <Button
                variant="primary"
                onClick={handleAddToCart}
                disabled={!product.countInStock}
              >
                Add to Cart
              </Button>
            </div>
          </div>
        )}
        
        {userInfo?.isAdmin && (
          <Link
            to={`/admin/product/${product._id}/edit`}
            className="btn btn-outline-secondary btn-sm"
          >
            Edit Product
          </Link>
        )}
      </Card.Body>
    </Card>
  );
};

export default Product;
