import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import Product from './Product';
import { AppContext } from '../context/AppContext';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { state } = useContext(AppContext);
  const userInfo = state?.userInfo || null; // Safeguard against undefined state

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        console.log('Fetching products...'); // Debug log
        const { data } = await axios.get('http://localhost:5000/api/products', {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
          }
        });
        console.log('Products fetched:', data); // Debug log
        setProducts(data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching products:', err); // Debug log
        setError(err.response?.data?.message || err.message || 'Failed to fetch products');
        setLoading(false);
        setProducts([]); // Ensure products is empty array on error
      }
    };

    fetchProducts();
  }, []);

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger my-5">Error: {error}</div>;
  if (!products.length) return <div className="alert alert-info my-5">No products available</div>;

  return (
    <div className="container my-4">
      <h2 className="mb-4">Our Products</h2>
      <div className="row">
        {products.map((product) => (
          <div key={product._id} className="col-md-4 mb-4">
            <Product product={product} userInfo={userInfo} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
