import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import Product from './Product';
import { AppContext } from '../context/AppContext';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { state: { userInfo } } = useContext(AppContext);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Ensure REACT_APP_API_URL is properly set in your .env file
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
        const { data } = await axios.get(`${apiUrl}/api/products`);
        setProducts(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger my-5">Error: {error}</div>;

  return (
    <div className="container my-4">
      <div className="row">
        {products.map((product) => (
          <div key={product._id} className="col-md-4 mb-4">
            <Product product={product} userInfo={userInfo} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
