import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';
import PropTypes from 'prop-types';

const Product = ({ product }) => {
  const { state, addToCart } = useContext(AppContext);
  const { userInfo } = state || {};
  const [qty, setQty] = useState(1);

  // Handle missing product data
  if (!product) {
    return (
      <div className="card h-100">
        <div className="card-body">
          <p>Product information not available</p>
        </div>
      </div>
    );
  }

  const handleAddToCart = () => {
    if (typeof addToCart === 'function') {
      addToCart(product, Number(qty));
    } else {
      console.error('addToCart function is not available in context');
    }
  };

  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
  };

  return (
    <div className="card h-100">
      <img
        src={product.image || 'https://via.placeholder.com/300x200?text=No+Image'}
        className="card-img-top"
        alt={product.name}
        style={{ height: '200px', objectFit: 'cover' }}
        onError={handleImageError}
      />
      <div className="card-body d-flex flex-column">
        <h5 className="card-title">{product.name || 'Unnamed Product'}</h5>
        <p className="card-text">{product.description || 'No description available'}</p>
        <p className="card-text">${product.price || '0.00'}</p>
        
        {userInfo && (
          <>
            <div className="mt-auto">
              <div className="input-group mb-3">
                <input
                  type="number"
                  min="1"
                  value={qty}
                  onChange={(e) => setQty(Math.max(1, e.target.value))}
                  className="form-control"
                />
                <button
                  className="btn btn-primary"
                  type="button"
                  onClick={handleAddToCart}
                  disabled={!addToCart}
                >
                  Add to Cart
                </button>
              </div>
            </div>
            
            {userInfo.isAdmin && (
              <Link
                to={`/admin/product/${product._id}/edit`}
                className="btn btn-secondary"
              >
                Edit Product
              </Link>
            )}
          </>
        )}
      </div>
    </div>
  );
};

Product.propTypes = {
  product: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    name: PropTypes.string,
    description: PropTypes.string,
    price: PropTypes.number,
    image: PropTypes.string,
  }),
};

export default Product;
