import React, { useState, useContext } from 'react';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState(null);
  const { register } = useContext(AppContext);
  const navigate = useNavigate();
  const location = useLocation();

  const redirect = location.search ? location.search.split('=')[1] : '/';

  const submitHandler = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    try {
      await register(name, email, password);
      navigate(redirect);
    } catch (err) {
      setError(err);
    }
  };

  return (
    <div className="register-container">
      <div className="register-row">
        <div className="register-col">
          <div className="register-card">
            <div className="card-body">
              <h2 className="register-title">Create Account</h2>
              {error && (
                <div className="register-error">
                  <i className="bi bi-exclamation-triangle-fill me-2"></i>
                  {error}
                </div>
              )}
              <form onSubmit={submitHandler}>
                <div className="form-group">
                  <label htmlFor="name" className="form-label">
                    Full Name
                  </label>
                  <input
                    type="text"
                    className="form-input"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email" className="form-label">
                    Email Address
                  </label>
                  <input
                    type="email"
                    className="form-input"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="password" className="form-label">
                    Password
                  </label>
                  <input
                    type="password"
                    className="form-input"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="confirmPassword" className="form-label">
                    Confirm Password
                  </label>
                  <input
                    type="password"
                    className={`form-input ${error?.toLowerCase().includes('password') ? 'is-invalid' : ''}`}
                    id="confirmPassword"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                  />
                </div>
                <button type="submit" className="register-button">
                  Create Account
                </button>
              </form>
              <div className="register-footer">
                Already have an account?{' '}
                <Link 
                  to={redirect ? `/login?redirect=${redirect}` : '/login'}
                  className="login-link"
                >
                  Sign In
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>

      <style jsx>{`
        /* Base Styles */
        .register-container {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          padding: 2rem;
          background: linear-gradient(135deg, #f8f9fa 0%, #e6f7f6 100%);
        }

        .register-row {
          display: flex;
          justify-content: center;
          width: 100%;
          max-width: 1200px;
        }

        .register-col {
          width: 100%;
          max-width: 450px;
        }

        /* Card Styles */
        .register-card {
          background: white;
          border-radius: 12px;
          box-shadow: 0 10px 30px rgba(34, 201, 190, 0.15);
          overflow: hidden;
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          border: none;
        }

        .register-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 15px 35px rgba(34, 201, 190, 0.2);
        }

        .card-body {
          padding: 2.5rem;
        }

        /* Title Styles */
        .register-title {
          color: #22c9be;
          text-align: center;
          margin-bottom: 2rem;
          font-weight: 700;
          font-size: 1.8rem;
          position: relative;
        }

        .register-title::after {
          content: '';
          display: block;
          width: 80px;
          height: 3px;
          background: #22c9be;
          margin: 0.5rem auto 0;
          border-radius: 3px;
        }

        /* Error Message */
        .register-error {
          background: #ffebee;
          color: #d32f2f;
          padding: 0.75rem 1rem;
          border-radius: 6px;
          margin-bottom: 1.5rem;
          display: flex;
          align-items: center;
          font-size: 0.9rem;
          animation: fadeIn 0.3s ease-out;
        }

        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        /* Form Styles */
        .form-group {
          margin-bottom: 1.5rem;
        }

        .form-label {
          display: block;
          margin-bottom: 0.5rem;
          color: #555;
          font-weight: 500;
          font-size: 0.95rem;
        }

        .form-input {
          width: 100%;
          padding: 0.75rem 1rem;
          border: 1px solid #ddd;
          border-radius: 6px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background-color: #fafafa;
        }

        .form-input:focus {
          border-color: #22c9be;
          box-shadow: 0 0 0 3px rgba(34, 201, 190, 0.2);
          outline: none;
          background-color: white;
        }

        .form-input.is-invalid {
          border-color: #ff6b6b;
          background-color: #fff9f9;
        }

        .form-input.is-invalid:focus {
          box-shadow: 0 0 0 3px rgba(255, 107, 107, 0.2);
        }

        /* Button Styles */
        .register-button {
          width: 100%;
          padding: 0.75rem;
          background: linear-gradient(135deg, #22c9be 0%, #1aa89f 100%);
          color: white;
          border: none;
          border-radius: 6px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          margin-top: 0.5rem;
        }

        .register-button:hover {
          background: linear-gradient(135deg, #1aa89f 0%, #22c9be 100%);
          transform: translateY(-2px);
          box-shadow: 0 4px 12px rgba(34, 201, 190, 0.3);
        }

        /* Footer Links */
        .register-footer {
          text-align: center;
          margin-top: 1.5rem;
          color: #666;
          font-size: 0.9rem;
        }

        .login-link {
          color: #22c9be;
          font-weight: 500;
          text-decoration: none;
          transition: all 0.2s ease;
        }

        .login-link:hover {
          color: #1aa89f;
          text-decoration: underline;
        }

        /* Password Match Indicator */
        .password-match {
          margin-top: 0.25rem;
          font-size: 0.8rem;
          display: flex;
          align-items: center;
        }

        .password-match.valid {
          color: #4caf50;
        }

        .password-match.invalid {
          color: #f44336;
        }

        /* Responsive Adjustments */
        @media (max-width: 576px) {
          .register-container {
            padding: 1rem;
          }
          
          .card-body {
            padding: 1.5rem;
          }
          
          .register-title {
            font-size: 1.5rem;
          }
        }
      `}</style>
    </div>
  );
};

export default Register;
