import React, { useEffect, useState, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const OrderConfirmation = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sdkReady, setSdkReady] = useState(false);
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  useEffect(() => {
    const addPayPalScript = () => {
      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.REACT_APP_PAYPAL_CLIENT_ID}`;
      script.async = true;
      script.onload = () => {
        setSdkReady(true);
      };
      document.body.appendChild(script);
    };

    const fetchOrder = async () => {
      try {
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/orders/${id}`,
          {
            headers: {
              Authorization: `Bearer ${userInfo.token}`,
            },
          }
        );
        setOrder(data);
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || err.message);
        setLoading(false);
      }
    };

    if (!userInfo) {
      navigate('/login');
    } else {
      if (!order || (order && order._id !== id)) {
        fetchOrder();
      }
      if (!window.paypal && !sdkReady) {
        addPayPalScript();
      } else {
        setSdkReady(true);
      }
    }
  }, [id, userInfo, navigate, order, sdkReady]);

  const successPaymentHandler = async (paymentResult) => {
    try {
      const { data } = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/orders/${id}/pay`,
        paymentResult,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      setOrder(data);
      // Simple alert since we removed toastify
      alert('Payment was successful!');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  };

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger my-5">{error}</div>;

  return (
    <div className="container my-5">
      <h2 className="mb-4">Order #{order._id}</h2>
      <div className="row">
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Shipping</h4>
              <p>
                <strong>Address:</strong> {order.shippingAddress.address}, {order.shippingAddress.city}{' '}
                {order.shippingAddress.postalCode}, {order.shippingAddress.country}
              </p>
            </div>
          </div>
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Payment Method</h4>
              <p>
                <strong>Method:</strong> {order.paymentMethod}
              </p>
              {order.isPaid ? (
                <div className="alert alert-success">
                  Paid on {new Date(order.paidAt).toLocaleDateString()}
                </div>
              ) : (
                <div className="alert alert-warning">Not Paid</div>
              )}
            </div>
          </div>
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Order Items</h4>
              {order.orderItems.map((item) => (
                <div key={item._id} className="d-flex justify-content-between mb-3">
                  <div className="d-flex align-items-center">
                    <img
                      src={item.image}
                      alt={item.name}
                      style={{ width: '50px', height: '50px', objectFit: 'cover', marginRight: '15px' }}
                    />
                    <span>{item.name}</span>
                  </div>
                  <div>
                    {item.qty} x ${item.price} = ${(item.qty * item.price).toFixed(2)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Order Summary</h4>
              <div className="d-flex justify-content-between mb-2">
                <span>Items:</span>
                <span>${order.itemsPrice.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Shipping:</span>
                <span>${order.shippingPrice.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Tax:</span>
                <span>${order.taxPrice.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between fw-bold">
                <span>Total:</span>
                <span>${order.totalPrice.toFixed(2)}</span>
              </div>
              {!order.isPaid && (
                <div className="mt-3">
                  <button 
                    className="btn btn-primary w-100"
                    onClick={() => {
                      // Simulate payment success
                      successPaymentHandler({
                        id: 'simulated_payment_id',
                        status: 'COMPLETED',
                        update_time: new Date().toISOString(),
                        email_address: userInfo.email
                      });
                    }}
                  >
                    Mark as Paid (Simulation)
                  </button>
                  <small className="text-muted d-block mt-2">
                    Note: This is a simulation. In a real app, implement PayPal or Stripe integration.
                  </small>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderConfirmation;
