import React from 'react';
import { createBrowserRouter, RouterProvider, Outlet, Navigate } from 'react-router-dom';
import { AppProvider } from './context/AppContext';
import {
  Header,
  ProductList,
  ProductDetails,
  Cart,
  Login,
  Register,
  Shipping,
  Payment,
  PlaceOrder,
  Order,
  Profile,
  AdminPanel,
  ProductCreate,
  ProductEdit,
  NotFound
} from './components';
import './App.css';

const Layout = () => (
  <>
    <Header />
    <main className="py-3">
      <div className="container">
        <Outlet />
      </div>
    </main>
  </>
);

const ProtectedRoute = ({ children }) => {
  const { state } = React.useContext(AppContext);
  return state.userInfo ? children : <Navigate to="/login" />;
};

const AdminRoute = ({ children }) => {
  const { state } = React.useContext(AppContext);
  return state.userInfo?.isAdmin ? children : <Navigate to="/login" />;
};

const router = createBrowserRouter([
  {
    element: <Layout />,
    children: [
      { path: '/', element: <ProductList /> },
      { path: '/product/:id', element: <ProductDetails /> },
      { path: '/cart/:id?', element: <Cart /> },
      { path: '/login', element: <Login /> },
      { path: '/register', element: <Register /> },
      {
        path: '/shipping',
        element: (
          <ProtectedRoute>
            <Shipping />
          </ProtectedRoute>
        )
      },
      {
        path: '/payment',
        element: (
          <ProtectedRoute>
            <Payment />
          </ProtectedRoute>
        )
      },
      {
        path: '/placeorder',
        element: (
          <ProtectedRoute>
            <PlaceOrder />
          </ProtectedRoute>
        )
      },
      {
        path: '/order/:id',
        element: (
          <ProtectedRoute>
            <Order />
          </ProtectedRoute>
        )
      },
      {
        path: '/profile',
        element: (
          <ProtectedRoute>
            <Profile />
          </ProtectedRoute>
        )
      },
      {
        path: '/admin',
        element: (
          <AdminRoute>
            <AdminPanel />
          </AdminRoute>
        )
      },
      {
        path: '/admin/product/create',
        element: (
          <AdminRoute>
            <ProductCreate />
          </AdminRoute>
        )
      },
      {
        path: '/admin/product/:id/edit',
        element: (
          <AdminRoute>
            <ProductEdit />
          </AdminRoute>
        )
      },
      { path: '*', element: <NotFound /> }
    ]
  }
]);

function App() {
  return (
    <AppProvider>
      <RouterProvider router={router} />
    </AppProvider>
  );
}

export default App;
