import React, { useState, useEffect, useContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const ProductEdit = () => {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    name: '',
    price: 0,
    description: '',
    image: '',
    stock: 0,
    category: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [imageError, setImageError] = useState(false);
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  // Sample product data (30 items)
  const sampleProducts = [
    {
      _id: '1',
      name: 'Wireless Bluetooth Headphones',
      price: 89.99,
      description: 'High-quality wireless headphones with noise cancellation',
      image: '/images/headphones.jpg',
      stock: 25,
      category: 'Electronics'
    },
    {
      _id: '2',
      name: 'Smart Fitness Watch',
      price: 129.99,
      description: 'Track your workouts and health metrics',
      image: '/images/smartwatch.jpg',
      stock: 18,
      category: 'Electronics'
    },
    // ... (28 more sample products)
  ];

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        // First try to fetch from API
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/products/${id}`
        );
        setFormData({
          name: data.name,
          price: data.price,
          description: data.description,
          image: data.image,
          stock: data.stock,
          category: data.category || ''
        });
      } catch (err) {
        // Fallback to sample data if API fails
        const sampleProduct = sampleProducts.find(p => p._id === id);
        if (sampleProduct) {
          setFormData({
            name: sampleProduct.name,
            price: sampleProduct.price,
            description: sampleProduct.description,
            image: sampleProduct.image,
            stock: sampleProduct.stock,
            category: sampleProduct.category
          });
        } else {
          setError('Product not found');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'price' || name === 'stock' ? Number(value) : value
    }));
  };

  const validateImage = (url) => {
    const img = new Image();
    img.onerror = () => setImageError(true);
    img.onload = () => setImageError(false);
    img.src = url;
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    
    if (imageError) {
      setError('Please provide a valid image URL');
      return;
    }

    try {
      setLoading(true);
      setError(null);
      
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/products/${id}`,
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      
      navigate('/admin');
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Update failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container my-5">
      <h2>Edit Product</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      
      <form onSubmit={submitHandler}>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Price</label>
          <input
            type="number"
            className="form-control"
            name="price"
            min="0"
            step="0.01"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            className="form-control"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Image URL</label>
          <input
            type="url"
            className="form-control"
            name="image"
            value={formData.image}
            onChange={(e) => {
              handleChange(e);
              validateImage(e.target.value);
            }}
            required
          />
          {imageError && <div className="text-danger mt-1">Image URL appears invalid</div>}
          {formData.image && !imageError && (
            <div className="mt-2">
              <img 
                src={formData.image} 
                alt="Preview" 
                style={{ maxHeight: '200px' }}
                onError={() => setImageError(true)}
              />
            </div>
          )}
        </div>

        <div className="mb-3">
          <label className="form-label">Stock</label>
          <input
            type="number"
            className="form-control"
            name="stock"
            min="0"
            value={formData.stock}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Category</label>
          <select
            className="form-select"
            name="category"
            value={formData.category}
            onChange={handleChange}
          >
            <option value="">Select Category</option>
            <option value="Electronics">Electronics</option>
            <option value="Clothing">Clothing</option>
            <option value="Home">Home</option>
            <option value="Sports">Sports</option>
            <option value="Books">Books</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" role="status"></span>
              Updating...
            </>
          ) : 'Update Product'}
        </button>
      </form>
    </div>
  );
};

export default ProductEdit;
