import React, { useEffect, useState, useContext, useRef } from 'react';
import { Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';
import axios from 'axios';

const OrderHistory = () => {
  const { state } = useContext(AppContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const abortControllerRef = useRef(new AbortController());

  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Cancel any pending request
      abortControllerRef.current.abort();
      abortControllerRef.current = new AbortController();

      const { data } = await axios.get('/api/orders/myorders', {
        signal: abortControllerRef.current.signal,
        headers: {
          Authorization: `Bearer ${state.userInfo.token}`
        }
      });

      setOrders(data);
    } catch (err) {
      if (!axios.isCancel(err)) {
        setError(err.response?.data?.message || 'Failed to load orders');
        setOrders([]);
      }
    } finally {
      if (!abortControllerRef.current.signal.aborted) {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    if (state.userInfo?.token) {
      fetchOrders();
    }

    return () => {
      abortControllerRef.current.abort();
    };
  }, [state.userInfo?.token]);

  const handleImageError = (e) => {
    e.target.src = '/images/placeholder-product.png';
  };

  const handleRetry = () => {
    fetchOrders();
  };

  if (loading) {
    return (
      <div className="container my-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading your orders...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
        <button 
          className="btn btn-primary" 
          onClick={handleRetry}
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <h2 className="mb-4">Your Order History</h2>
      {orders.length === 0 && !error ? (
        <div className="alert alert-info">
          You haven't placed any orders yet. <Link to="/">Start shopping!</Link>
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>Order</th>
                <th>Date</th>
                <th>Total</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order._id}>
                  <td>
                    <div className="d-flex align-items-center">
                      {order.orderItems[0]?.image && (
                        <img
                          src={order.orderItems[0].image}
                          alt={order.orderItems[0].name}
                          className="img-thumbnail me-3"
                          style={{ width: '60px', height: '60px', objectFit: 'cover' }}
                          onError={handleImageError}
                        />
                      )}
                      <div>
                        <div className="fw-bold">#{order._id.substring(18, 24)}</div>
                        <small>{order.orderItems.length} items</small>
                      </div>
                    </div>
                  </td>
                  <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                  <td>${order.totalPrice.toFixed(2)}</td>
                  <td>
                    <span className={`badge ${order.isPaid ? 'bg-success' : 'bg-warning'}`}>
                      {order.isPaid ? 'Completed' : 'Processing'}
                    </span>
                  </td>
                  <td>
                    <Link 
                      to={`/order/${order._id}`} 
                      className="btn btn-sm btn-outline-primary"
                    >
                      View Details
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default OrderHistory;
