import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppContext } from '../context/AppContext';
import { Dropdown } from 'react-bootstrap'; // Import Bootstrap Dropdown component

const Header = () => {
  const { state, logout } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <header className="bg-dark text-white p-3">
      <div className="container">
        <div className="d-flex justify-content-between align-items-center">
          <Link to="/" className="text-white text-decoration-none">
            <h1>ShopEasy</h1>
          </Link>
          <div className="d-flex align-items-center gap-3">
            <Link to="/cart" className="text-white text-decoration-none">
              <i className="bi bi-cart-fill me-1"></i> Cart
            </Link>
            
            {userInfo ? (
              <Dropdown>
                <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                  <i className="bi bi-person-fill me-1"></i> {userInfo.name}
                </Dropdown.Toggle>

                <Dropdown.Menu align="end">
                  <Dropdown.Item as={Link} to="/profile">
                    <i className="bi bi-person me-2"></i> Profile
                  </Dropdown.Item>
                  <Dropdown.Item as={Link} to="/order-history">
                    <i className="bi bi-list-check me-2"></i> My Orders
                  </Dropdown.Item>
                  {userInfo.isAdmin && (
                    <Dropdown.Item as={Link} to="/admin">
                      <i className="bi bi-shield-lock me-2"></i> Admin Panel
                    </Dropdown.Item>
                  )}
                  <Dropdown.Divider />
                  <Dropdown.Item onClick={handleLogout}>
                    <i className="bi bi-box-arrow-right me-2"></i> Logout
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            ) : (
              <Link to="/login" className="text-white text-decoration-none">
                <i className="bi bi-box-arrow-in-right me-1"></i> Login
              </Link>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
