import React, { useEffect, useState, useMemo } from 'react';
import Product from './Product';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Memoized sample products data (30 products)
  const sampleProducts = useMemo(() => [
    {
      _id: '1',
      name: 'Echo Dot (4th Gen) | Smart speaker with Alexa',
      description: 'Meet the Echo Dot - Our most popular smart speaker with Alexa.',
      price: 49.99,
      image: 'https://m.media-amazon.com/images/I/71Q9d6N7xkL._AC_SL1000_.jpg',
      countInStock: 15
    },
    {
      _id: '2',
      name: 'Fire TV Stick 4K streaming device',
      description: 'Cinematic experience - Watch in vibrant 4K Ultra HD.',
      price: 39.99,
      image: 'https://m.media-amazon.com/images/I/51CgKGfMelL._AC_SL1000_.jpg',
      countInStock: 20
    },
    // ... (include all 30 products from previous example)
    {
      _id: '30',
      name: 'Xbox Wireless Controller - Shock Blue',
      description: 'Bluetooth technology for wireless gaming.',
      price: 59.99,
      image: 'https://m.media-amazon.com/images/I/61XZQXFQeVL._AC_SL1000_.jpg',
      countInStock: 15
    }
  ], []);

  useEffect(() => {
    let isMounted = true;

    const fetchProducts = async () => {
      try {
        // API call would go here
        if (isMounted) {
          setProducts(sampleProducts);
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setProducts(sampleProducts);
          setLoading(false);
        }
      }
    };

    fetchProducts();

    return () => { isMounted = false };
  }, [sampleProducts]);

  if (loading) return <div className="text-center my-5">Loading...</div>;

  return (
    <div className="container my-4">
      <h2 className="mb-4">Featured Products</h2>
      <div className="row">
        {products.map((product) => (
          <div key={product._id} className="col-md-4 col-lg-3 mb-4">
            <Product product={product} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
