import React, { useEffect, useState, useContext, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const OrderConfirmation = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sdkReady, setSdkReady] = useState(false);
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  const successPaymentHandler = useCallback(async (paymentResult) => {
    try {
      const { data } = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/orders/${id}/pay`,
        paymentResult,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      setOrder(data);
      alert('Payment was successful!');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }, [id, userInfo.token]);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/orders/${id}`,
          {
            headers: {
              Authorization: `Bearer ${userInfo.token}`,
            },
          }
        );
        setOrder(data);
      } catch (err) {
        setError(err.response?.data?.message || err.message);
      } finally {
        setLoading(false);
      }
    };

    if (!userInfo) {
      navigate('/login');
    } else {
      fetchOrder();
    }
  }, [id, userInfo, navigate]);

  useEffect(() => {
    if (!order || order.isPaid || !process.env.REACT_APP_PAYPAL_CLIENT_ID) return;

    const loadPayPalSDK = () => {
      if (window.paypal) {
        setSdkReady(true);
        return;
      }

      const script = document.createElement('script');
      script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.REACT_APP_PAYPAL_CLIENT_ID}&currency=USD&disable-funding=credit,card`;
      script.async = true;
      script.setAttribute('data-testid', 'paypal-script'); // Fixed dataset attribute
      
      script.onload = () => {
        console.log('PayPal SDK loaded successfully');
        setSdkReady(true);
      };
      
      script.onerror = () => {
        console.error('Failed to load PayPal SDK');
        setSdkReady(false);
        setError('Failed to load payment processor. Please try again later.');
      };

      document.body.appendChild(script);

      return () => {
        document.body.removeChild(script);
      };
    };

    loadPayPalSDK();
  }, [order]);

  useEffect(() => {
    if (!sdkReady || !window.paypal || !order || order.isPaid) return;

    console.log('Initializing PayPal buttons');
    
    const button = window.paypal.Buttons({
      style: {
        layout: 'vertical',
        color: 'gold',
        shape: 'rect',
        label: 'checkout',
        height: 45
      },
      createOrder: (data, actions) => {
        return actions.order.create({
          purchase_units: [{
            amount: {
              value: order.totalPrice.toFixed(2),
              currency_code: 'USD',
              breakdown: {
                item_total: {
                  value: order.itemsPrice.toFixed(2),
                  currency_code: 'USD'
                },
                shipping: {
                  value: order.shippingPrice.toFixed(2),
                  currency_code: 'USD'
                },
                tax_total: {
                  value: order.taxPrice.toFixed(2),
                  currency_code: 'USD'
                }
              }
            },
            items: order.orderItems.map(item => ({
              name: item.name.substring(0, 127), // PayPal max length
              unit_amount: {
                value: item.price.toFixed(2),
                currency_code: 'USD'
              },
              quantity: item.qty.toString(),
              category: 'PHYSICAL_GOODS'
            }))
          }],
          application_context: {
            shipping_preference: 'SET_PROVIDED_ADDRESS'
          }
        });
      },
      onApprove: async (data, actions) => {
        try {
          const details = await actions.order.capture();
          console.log('Payment completed:', details);
          successPaymentHandler({
            id: details.id,
            status: details.status,
            update_time: details.update_time,
            email_address: details.payer.email_address,
            payer: details.payer
          });
        } catch (err) {
          console.error('Payment capture error:', err);
          alert('Payment processing failed. Please try again.');
        }
      },
      onError: (err) => {
        console.error('PayPal error:', err);
        alert(`Payment error: ${err.message}`);
      },
      onCancel: (data) => {
        console.log('Payment cancelled:', data);
      }
    });

    if (button.isEligible()) {
      button.render('#paypal-button-container').catch(err => {
        console.error('Error rendering PayPal button:', err);
        setError('Failed to initialize payment button. Please refresh the page.');
      });
    } else {
      console.warn('PayPal button not eligible');
      setError('Payment method not available. Please try another method.');
    }

    return () => {
      if (button.close) {
        button.close();
      }
    };
  }, [sdkReady, order, successPaymentHandler]);

  if (loading) return (
    <div className="text-center my-5">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
      <p className="mt-2">Loading order details...</p>
    </div>
  );

  if (error) return (
    <div className="container my-5">
      <div className="alert alert-danger">
        {error}
        <button 
          className="btn btn-outline-danger mt-2"
          onClick={() => window.location.reload()}
        >
          Try Again
        </button>
      </div>
    </div>
  );

  return (
    <div className="container my-5">
      <h2 className="mb-4">Order #{order?._id}</h2>
      <div className="row">
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Shipping</h4>
              <p>
                <strong>Address:</strong> {order?.shippingAddress?.address}, {order?.shippingAddress?.city}{' '}
                {order?.shippingAddress?.postalCode}, {order?.shippingAddress?.country}
              </p>
            </div>
          </div>
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Payment Method</h4>
              <p>
                <strong>Method:</strong> {order?.paymentMethod}
              </p>
              {order?.isPaid ? (
                <div className="alert alert-success">
                  Paid on {new Date(order.paidAt).toLocaleDateString()}
                </div>
              ) : (
                <div className="alert alert-warning">Not Paid</div>
              )}
            </div>
          </div>
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Order Items</h4>
              {order?.orderItems?.map((item) => (
                <div key={item._id} className="d-flex justify-content-between mb-3">
                  <div className="d-flex align-items-center">
                    <img
                      src={item.image}
                      alt={item.name}
                      style={{ width: '50px', height: '50px', objectFit: 'cover', marginRight: '15px' }}
                      onError={(e) => {
                        e.target.src = '/images/placeholder-product.png';
                      }}
                    />
                    <span>{item.name}</span>
                  </div>
                  <div>
                    {item.qty} x ${item.price} = ${(item.qty * item.price).toFixed(2)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Order Summary</h4>
              <div className="d-flex justify-content-between mb-2">
                <span>Items:</span>
                <span>${order?.itemsPrice?.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Shipping:</span>
                <span>${order?.shippingPrice?.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Tax:</span>
                <span>${order?.taxPrice?.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between fw-bold">
                <span>Total:</span>
                <span>${order?.totalPrice?.toFixed(2)}</span>
              </div>
              {!order?.isPaid && (
                <div className="mt-3">
                  {process.env.REACT_APP_PAYPAL_CLIENT_ID ? (
                    <>
                      <div id="paypal-button-container"></div>
                      <p className="text-muted small mt-2">
                        Test with PayPal sandbox credentials
                      </p>
                    </>
                  ) : (
                    <div className="alert alert-warning">
                      PayPal integration not configured
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderConfirmation;
