import React, { createContext, useReducer, useEffect } from 'react';
import axios from 'axios';

export const AppContext = createContext();

const initialState = {
  userInfo: localStorage.getItem('userInfo')
    ? JSON.parse(localStorage.getItem('userInfo'))
    : null,
  cart: {
    cartItems: localStorage.getItem('cartItems')
      ? JSON.parse(localStorage.getItem('cartItems'))
      : [],
    shippingAddress: localStorage.getItem('shippingAddress')
      ? JSON.parse(localStorage.getItem('shippingAddress'))
      : {},
    paymentMethod: localStorage.getItem('paymentMethod')
      ? localStorage.getItem('paymentMethod')
      : '',
  },
};

function reducer(state, action) {
  switch (action.type) {
    case 'USER_LOGIN':
      return { ...state, userInfo: action.payload };
    case 'USER_LOGOUT':
      return {
        ...state,
        userInfo: null,
        cart: { cartItems: [], shippingAddress: {}, paymentMethod: '' },
      };
    case 'ADD_TO_CART':
      const newItem = action.payload;
      const existItem = state.cart.cartItems.find(
        (item) => item._id === newItem._id
      );
      const cartItems = existItem
        ? state.cart.cartItems.map((item) =>
            item._id === existItem._id ? newItem : item
          )
        : [...state.cart.cartItems, newItem];
      localStorage.setItem('cartItems', JSON.stringify(cartItems));
      return { ...state, cart: { ...state.cart, cartItems } };
    case 'REMOVE_FROM_CART':
      const filteredItems = state.cart.cartItems.filter(
        (item) => item._id !== action.payload
      );
      localStorage.setItem('cartItems', JSON.stringify(filteredItems));
      return { ...state, cart: { ...state.cart, cartItems: filteredItems } };
    case 'SAVE_SHIPPING_ADDRESS':
      return {
        ...state,
        cart: { ...state.cart, shippingAddress: action.payload },
      };
    case 'SAVE_PAYMENT_METHOD':
      return {
        ...state,
        cart: { ...state.cart, paymentMethod: action.payload },
      };
    case 'CART_CLEAR':
      return { ...state, cart: { ...state.cart, cartItems: [] } };
    case 'UPDATE_USER_PROFILE':
      return {
        ...state,
        userInfo: { ...state.userInfo, ...action.payload },
      };
    default:
      return state;
  }
}

export function AppProvider({ children }) {
  const [state, dispatch] = useReducer(reducer, initialState);

  // Sync state with localStorage
  useEffect(() => {
    if (state.userInfo) {
      localStorage.setItem('userInfo', JSON.stringify(state.userInfo));
    } else {
      localStorage.removeItem('userInfo');
    }
  }, [state.userInfo]);

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(state.cart.cartItems));
  }, [state.cart.cartItems]);

  useEffect(() => {
    localStorage.setItem(
      'shippingAddress',
      JSON.stringify(state.cart.shippingAddress)
    );
  }, [state.cart.shippingAddress]);

  useEffect(() => {
    localStorage.setItem('paymentMethod', state.cart.paymentMethod);
  }, [state.cart.paymentMethod]);

  // Auth functions
  const login = async (email, password) => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const { data } = await axios.post(
        `${apiUrl}/api/users/login`,
        { email, password },
        { withCredentials: true }
      );
      dispatch({ type: 'USER_LOGIN', payload: data });
      return data;
    } catch (error) {
      const errorMessage = error.response?.data?.message || 
                         error.message || 
                         'Login failed. Please try again.';
      throw errorMessage;
    }
  };

  const logout = () => {
    dispatch({ type: 'USER_LOGOUT' });
  };

  const register = async (name, email, password) => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const { data } = await axios.post(
        `${apiUrl}/api/users`,
        { name, email, password },
        { withCredentials: true }
      );
      dispatch({ type: 'USER_LOGIN', payload: data });
      return data;
    } catch (error) {
      const errorMessage = error.response?.data?.message || 
                         error.message || 
                         'Registration failed. Please try again.';
      throw errorMessage;
    }
  };

  const updateUserProfile = async (userData) => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const { data } = await axios.put(
        `${apiUrl}/api/users/profile`,
        userData,
        {
          headers: {
            Authorization: `Bearer ${state.userInfo.token}`,
          },
          withCredentials: true,
        }
      );
      dispatch({ type: 'UPDATE_USER_PROFILE', payload: data });
      return data;
    } catch (error) {
      const errorMessage = error.response?.data?.message || 
                         error.message || 
                         'Profile update failed. Please try again.';
      throw errorMessage;
    }
  };

  // Cart functions
  const addToCart = (product, qty) => {
    dispatch({
      type: 'ADD_TO_CART',
      payload: {
        _id: product._id,
        name: product.name,
        image: product.image,
        price: product.price,
        countInStock: product.countInStock,
        qty: Math.min(qty, product.countInStock),
      },
    });
  };

  const removeFromCart = (id) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: id });
  };

  const saveShippingAddress = (data) => {
    dispatch({ type: 'SAVE_SHIPPING_ADDRESS', payload: data });
  };

  const savePaymentMethod = (method) => {
    dispatch({ type: 'SAVE_PAYMENT_METHOD', payload: method });
  };

  const clearCart = () => {
    dispatch({ type: 'CART_CLEAR' });
  };

  // Order functions
  const createOrder = async (orderData) => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const { data } = await axios.post(
        `${apiUrl}/api/orders`,
        orderData,
        {
          headers: {
            Authorization: `Bearer ${state.userInfo.token}`,
          },
          withCredentials: true,
        }
      );
      clearCart();
      return data;
    } catch (error) {
      const errorMessage = error.response?.data?.message || 
                         error.message || 
                         'Order creation failed. Please try again.';
      throw errorMessage;
    }
  };

  return (
    <AppContext.Provider
      value={{
        state,
        dispatch,
        login,
        logout,
        register,
        updateUserProfile,
        addToCart,
        removeFromCart,
        saveShippingAddress,
        savePaymentMethod,
        clearCart,
        createOrder,
      }}
    >
      {children}
    </AppContext.Provider>
  );
}
