import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const AdminPanel = () => {
  const { state } = useContext(AppContext);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const controller = new AbortController();

    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/products`,
          {
            headers: { 
              Authorization: `Bearer ${state.userInfo?.token}` 
            },
            signal: controller.signal
          }
        );

        // Handle both array and object response formats
        const productsData = Array.isArray(data) ? data : (data?.products || []);
        setProducts(productsData);
      } catch (err) {
        if (!axios.isCancel(err)) {
          console.error('Fetch products error:', err);
          setError(
            err.response?.data?.message || 
            err.message || 
            'Failed to load products. Please try again.'
          );
        }
      } finally {
        setLoading(false);
      }
    };

    if (state.userInfo?.isAdmin) {
      fetchProducts();
    }

    return () => controller.abort();
  }, [state.userInfo]);

  const handleDelete = async (productId) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/products/${productId}`,
        {
          headers: { 
            Authorization: `Bearer ${state.userInfo?.token}` 
          }
        }
      );
      setProducts(products.filter(p => p._id !== productId));
    } catch (err) {
      console.error('Delete product error:', err);
      alert(
        err.response?.data?.message || 
        'Failed to delete product. Please try again.'
      );
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center my-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container my-4">
        <div className="alert alert-danger">
          <h4>Error Loading Products</h4>
          <p>{error}</p>
          <div className="d-flex gap-2 mt-3">
            <button 
              className="btn btn-primary"
              onClick={() => window.location.reload()}
            >
              Refresh Page
            </button>
            <button 
              className="btn btn-outline-secondary"
              onClick={() => setError(null)}
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Product Management</h2>
        <Link 
          to="/admin/products/create" 
          className="btn btn-primary"
        >
          <i className="bi bi-plus-circle me-2"></i>
          Create Product
        </Link>
      </div>

      {products.length === 0 ? (
        <div className="alert alert-info">
          <div className="d-flex justify-content-between align-items-center">
            <span>No products found</span>
            <Link to="/admin/products/create" className="btn btn-sm btn-info">
              <i className="bi bi-plus-circle me-1"></i>
              Create First Product
            </Link>
          </div>
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-hover align-middle">
            <thead className="table-light">
              <tr>
                <th>Product</th>
                <th>Price</th>
                <th>Stock</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product._id}>
                  <td>
                    <div className="d-flex align-items-center gap-3">
                      <img 
                        src={product.image} 
                        alt={product.name}
                        className="rounded"
                        style={{width: '60px', height: '60px', objectFit: 'cover'}}
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = '/images/placeholder-product.png';
                        }}
                      />
                      <div>
                        <strong>{product.name}</strong>
                        <div className="text-muted small">
                          SKU: {product._id.substring(0, 8)}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td>${product.price.toFixed(2)}</td>
                  <td className={product.stock <= 0 ? 'text-danger fw-bold' : ''}>
                    {product.stock}
                  </td>
                  <td>
                    <div className="d-flex gap-2">
                      <Link
                        to={`/admin/products/${product._id}/edit`}
                        className="btn btn-sm btn-outline-primary"
                      >
                        <i className="bi bi-pencil me-1"></i>
                        Edit
                      </Link>
                      <button
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDelete(product._id)}
                      >
                        <i className="bi bi-trash me-1"></i>
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
