import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const ProductEdit = () => {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    name: '',
    price: 0,
    description: '',
    image: '',
    stock: 0,
    isFeatured: false
  });
  const [loading, setLoading] = useState({
    fetching: false,
    updating: false
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  // Verify API endpoint is reachable
  const verifyBackendConnection = async () => {
    try {
      await axios.get(`${process.env.REACT_APP_API_URL}/api/health`);
      return true;
    } catch (err) {
      console.error('Backend connection failed:', err);
      setError('Cannot connect to server. Please try again later.');
      return false;
    }
  };

  // Moved handleApiError inside useCallback to stabilize the reference
  const handleApiError = useCallback((err, action) => {
    console.error(`Error ${action} product:`, err);
    
    if (err.response) {
      switch (err.response.status) {
        case 401:
          setError('Session expired. Please login again.');
          navigate('/login');
          break;
        case 404:
          setError('Product not found. It may have been deleted.');
          navigate('/admin/products');
          break;
        case 500:
          setError('Server error. Please try again later.');
          break;
        default:
          setError(err.response.data?.message || 'An error occurred');
      }
    } else if (err.request) {
      setError('No response from server. Check your connection.');
    } else {
      setError(err.message || 'An unexpected error occurred');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProduct = async () => {
      if (!userInfo?.isAdmin) {
        navigate('/login');
        return;
      }

      if (!(await verifyBackendConnection())) return;

      try {
        setLoading(prev => ({ ...prev, fetching: true }));
        setError(null);
        
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/products/${id}`,
          {
            headers: { Authorization: `Bearer ${userInfo.token}` },
            timeout: 5000
          }
        );

        if (!data) {
          throw new Error('Product data not received');
        }

        setFormData({
          name: data.name || '',
          price: data.price || 0,
          description: data.description || '',
          image: data.image || '',
          stock: data.stock || data.countInStock || 0,
          isFeatured: data.isFeatured || false
        });
      } catch (err) {
        handleApiError(err, 'fetch');
      } finally {
        setLoading(prev => ({ ...prev, fetching: false }));
      }
    };

    fetchProduct();
  }, [id, userInfo, navigate, handleApiError]); // Added handleApiError to dependencies

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!(await verifyBackendConnection())) return;

    try {
      setLoading(prev => ({ ...prev, updating: true }));
      setError(null);
      
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/products/${id}`,
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`
          },
          timeout: 5000
        }
      );

      setSuccess('Product updated successfully!');
      setTimeout(() => navigate('/admin/products'), 2000);
    } catch (err) {
      handleApiError(err, 'updating');
    } finally {
      setLoading(prev => ({ ...prev, updating: false }));
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : type === 'number' ? Number(value) : value
    }));
  };

  if (loading.fetching) {
    return (
      <div className="container my-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p>Loading product details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger">
          <i className="bi bi-exclamation-triangle-fill me-2"></i>
          {error}
          <button 
            className="btn btn-sm btn-outline-danger ms-3"
            onClick={() => window.location.reload()}
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <div className="card shadow">
        <div className="card-body">
          <h2 className="card-title mb-4">Edit Product</h2>
          
          {success && (
            <div className="alert alert-success">
              <i className="bi bi-check-circle-fill me-2"></i>
              {success}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Product Name
              </label>
              <input
                type="text"
                className="form-control"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                minLength={3}
              />
            </div>

            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="price" className="form-label">
                  Price ($)
                </label>
                <input
                  type="number"
                  className="form-control"
                  id="price"
                  name="price"
                  value={formData.price}
                  onChange={handleChange}
                  required
                  min="0"
                  step="0.01"
                />
              </div>

              <div className="col-md-6 mb-3">
                <label htmlFor="stock" className="form-label">
                  Stock Quantity
                </label>
                <input
                  type="number"
                  className="form-control"
                  id="stock"
                  name="stock"
                  value={formData.stock}
                  onChange={handleChange}
                  required
                  min="0"
                />
              </div>
            </div>

            <div className="mb-3">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              <textarea
                className="form-control"
                id="description"
                name="description"
                rows="5"
                value={formData.description}
                onChange={handleChange}
                required
                minLength={10}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="image" className="form-label">
                Image URL
              </label>
              <input
                type="url"
                className="form-control"
                id="image"
                name="image"
                value={formData.image}
                onChange={handleChange}
                required
                placeholder="https://example.com/image.jpg"
              />
              {formData.image && (
                <div className="mt-2">
                  <img 
                    src={formData.image} 
                    alt="Preview" 
                    className="img-thumbnail" 
                    style={{ maxHeight: '200px' }}
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/300x300?text=Image+Not+Available';
                    }}
                  />
                </div>
              )}
            </div>

            <div className="mb-3 form-check">
              <input
                type="checkbox"
                className="form-control-check"
                id="isFeatured"
                name="isFeatured"
                checked={formData.isFeatured}
                onChange={handleChange}
              />
              <label className="form-check-label" htmlFor="isFeatured">
                Featured Product
              </label>
            </div>

            <div className="d-flex justify-content-between mt-4">
              <button
                type="button"
                className="btn btn-outline-secondary"
                onClick={() => navigate('/admin/products')}
                disabled={loading.updating}
              >
                Back to Products
              </button>
              <button
                type="submit"
                className="btn btn-primary"
                disabled={loading.updating}
              >
                {loading.updating ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    Saving...
                  </>
                ) : 'Update Product'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductEdit;
