import React, { createContext, useReducer, useEffect } from 'react';
import axios from 'axios';

export const AppContext = createContext();

// Helper function to safely parse localStorage items
const safeParse = (item, defaultValue) => {
  try {
    return item ? JSON.parse(item) : defaultValue;
  } catch (error) {
    console.error('Error parsing localStorage item:', error);
    return defaultValue;
  }
};

const initialState = {
  userInfo: safeParse(localStorage.getItem('userInfo'), null),
  cart: {
    cartItems: safeParse(localStorage.getItem('cartItems'), []),
    shippingAddress: safeParse(localStorage.getItem('shippingAddress'), {}),
    paymentMethod: localStorage.getItem('paymentMethod') || '',
  },
};

function reducer(state, action) {
  switch (action.type) {
    case 'USER_LOGIN':
      return { ...state, userInfo: action.payload };
    case 'USER_LOGOUT':
      return {
        ...state,
        userInfo: null,
        cart: { cartItems: [], shippingAddress: {}, paymentMethod: '' },
      };
    case 'ADD_TO_CART': {
      const newItem = action.payload;
      const existItem = state.cart.cartItems.find(
        (item) => item._id === newItem._id
      );
      const cartItems = existItem
        ? state.cart.cartItems.map((item) =>
            item._id === existItem._id ? newItem : item
          )
        : [...state.cart.cartItems, newItem];
      return { ...state, cart: { ...state.cart, cartItems } };
    }
    case 'REMOVE_FROM_CART': {
      const filteredItems = state.cart.cartItems.filter(
        (item) => item._id !== action.payload
      );
      return { ...state, cart: { ...state.cart, cartItems: filteredItems } };
    }
    case 'SAVE_SHIPPING_ADDRESS':
      return {
        ...state,
        cart: { ...state.cart, shippingAddress: action.payload },
      };
    case 'SAVE_PAYMENT_METHOD':
      return {
        ...state,
        cart: { ...state.cart, paymentMethod: action.payload },
      };
    case 'CART_CLEAR':
      return { ...state, cart: { ...state.cart, cartItems: [] } };
    case 'UPDATE_USER_PROFILE':
      return {
        ...state,
        userInfo: { ...state.userInfo, ...action.payload },
      };
    default:
      return state;
  }
}

export function AppProvider({ children }) {
  const [state, dispatch] = useReducer(reducer, initialState);

  // Sync state with localStorage
  useEffect(() => {
    try {
      if (state.userInfo) {
        localStorage.setItem('userInfo', JSON.stringify(state.userInfo));
      } else {
        localStorage.removeItem('userInfo');
      }
    } catch (error) {
      console.error('Error saving userInfo to localStorage:', error);
    }
  }, [state.userInfo]);

  useEffect(() => {
    try {
      localStorage.setItem('cartItems', JSON.stringify(state.cart.cartItems));
    } catch (error) {
      console.error('Error saving cartItems to localStorage:', error);
    }
  }, [state.cart.cartItems]);

  useEffect(() => {
    try {
      localStorage.setItem(
        'shippingAddress',
        JSON.stringify(state.cart.shippingAddress)
      );
    } catch (error) {
      console.error('Error saving shippingAddress to localStorage:', error);
    }
  }, [state.cart.shippingAddress]);

  useEffect(() => {
    try {
      if (state.cart.paymentMethod) {
        localStorage.setItem('paymentMethod', state.cart.paymentMethod);
      } else {
        localStorage.removeItem('paymentMethod');
      }
    } catch (error) {
      console.error('Error saving paymentMethod to localStorage:', error);
    }
  }, [state.cart.paymentMethod]);

  // Auth functions
  const login = async (email, password) => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const { data } = await axios.post(
        `${apiUrl}/api/users/login`,
        { email, password },
        { 
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
          }
        }
      );
      dispatch({ type: 'USER_LOGIN', payload: data });
      return data;
    } catch (error) {
      const errorMessage = error.response?.data?.message || 
                         error.message || 
                         'Login failed. Please try again.';
      console.error('Login error:', errorMessage);
      throw new Error(errorMessage);
    }
  };

  const logout = () => {
    dispatch({ type: 'USER_LOGOUT' });
  };

  // ... (keep other functions the same, but add similar error handling)

  // Cart functions
  const addToCart = (product, qty) => {
    if (!product || !product._id) {
      console.error('Invalid product data:', product);
      return;
    }
    
    dispatch({
      type: 'ADD_TO_CART',
      payload: {
        _id: product._id,
        name: product.name,
        image: product.image,
        price: product.price,
        countInStock: product.countInStock,
        qty: Math.min(qty, product.countInStock || 1),
      },
    });
  };

  // ... (rest of your functions)

  return (
    <AppContext.Provider
      value={{
        state,
        dispatch,
        login,
        logout,
        register,
        updateUserProfile,
        addToCart,
        removeFromCart,
        saveShippingAddress,
        savePaymentMethod,
        clearCart,
        createOrder,
      }}
    >
      {children}
    </AppContext.Provider>
  );
}
