import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const Checkout = () => {
  const { state, saveShippingAddress, savePaymentMethod, clearCart } = useContext(AppContext);
  const { cartItems } = state.cart;
  const { userInfo } = state;
  const navigate = useNavigate();

  const [address, setAddress] = useState(state.cart.shippingAddress?.address || '');
  const [city, setCity] = useState(state.cart.shippingAddress?.city || '');
  const [postalCode, setPostalCode] = useState(state.cart.shippingAddress?.postalCode || '');
  const [country, setCountry] = useState(state.cart.shippingAddress?.country || '');
  const [paymentMethod, setPaymentMethod] = useState(state.cart.paymentMethod || 'PayPal');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const submitHandler = async (e) => {
    e.preventDefault();
    saveShippingAddress({ address, city, postalCode, country });
    savePaymentMethod(paymentMethod);

    try {
      setLoading(true);

      const transformedCartItems = cartItems.map((item) => ({
        name: item.name,
        qty: item.qty,
        price: item.price,
        image: item.image,
        product: item._id,
      }));

      const { data } = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/orders`,
        {
          orderItems: transformedCartItems,
          shippingAddress: { address, city, postalCode, country },
          paymentMethod,
          itemsPrice: cartItems.reduce((acc, item) => acc + item.price * item.qty, 0),
          taxPrice: 0,
          shippingPrice: 0,
          totalPrice: cartItems.reduce((acc, item) => acc + item.price * item.qty, 0),
        },
        {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      clearCart();
      navigate(`/order/${data._id}`);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
      setLoading(false);
    }
  };

  return (
    <div className="container my-4">
      <h2>Checkout</h2>
      <div className="row">
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Shipping</h4>
              <form onSubmit={submitHandler}>
                <div className="mb-3">
                  <label htmlFor="address" className="form-label">Address</label>
                  <input type="text" className="form-control" id="address" value={address} onChange={(e) => setAddress(e.target.value)} required />
                </div>
                <div className="mb-3">
                  <label htmlFor="city" className="form-label">City</label>
                  <input type="text" className="form-control" id="city" value={city} onChange={(e) => setCity(e.target.value)} required />
                </div>
                <div className="mb-3">
                  <label htmlFor="postalCode" className="form-label">Postal Code</label>
                  <input type="text" className="form-control" id="postalCode" value={postalCode} onChange={(e) => setPostalCode(e.target.value)} required />
                </div>
                <div className="mb-3">
                  <label htmlFor="country" className="form-label">Country</label>
                  <input type="text" className="form-control" id="country" value={country} onChange={(e) => setCountry(e.target.value)} required />
                </div>
                <div className="mb-3">
                  <h4 className="card-title">Payment Method</h4>
                  <div className="form-check">
                    <input className="form-check-input" type="radio" name="paymentMethod" id="paypal" value="PayPal" checked={paymentMethod === 'PayPal'} onChange={(e) => setPaymentMethod(e.target.value)} />
                    <label className="form-check-label" htmlFor="paypal">PayPal</label>
                  </div>
                  <div className="form-check">
                    <input className="form-check-input" type="radio" name="paymentMethod" id="stripe" value="Stripe" checked={paymentMethod === 'Stripe'} onChange={(e) => setPaymentMethod(e.target.value)} />
                    <label className="form-check-label" htmlFor="stripe">Stripe</label>
                  </div>
                </div>
                <button type="submit" className="btn btn-primary" disabled={loading}>{loading ? 'Processing...' : 'Continue'}</button>
                {error && <div className="alert alert-danger mt-3">{error}</div>}
              </form>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Order Summary</h5>
              <ul className="list-group list-group-flush">
                {cartItems.map((item) => (
                  <li key={item._id} className="list-group-item d-flex justify-content-between align-items-center">
                    {item.name} x {item.qty} <span>${(item.price * item.qty).toFixed(2)}</span>
                  </li>
                ))}
                <li className="list-group-item d-flex justify-content-between align-items-center fw-bold">
                  Total <span>${cartItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)}</span>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
