import React, { useEffect, useState, useContext, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const OrderConfirmation = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sdkReady, setSdkReady] = useState(false);
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const navigate = useNavigate();

  const successPaymentHandler = useCallback(async (paymentResult) => {
    try {
      const { data } = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/orders/${id}/pay`,
        paymentResult,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      setOrder(data);
      alert('Payment was successful!');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }, [id, userInfo.token]);

  useEffect(() => {
    const addPayPalScript = () => {
      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.REACT_APP_PAYPAL_CLIENT_ID}&currency=USD`;
      script.async = true;
      script.onload = () => {
        setSdkReady(true);
      };
      script.onerror = () => {
        console.error('PayPal SDK failed to load');
        setSdkReady(false);
      };
      document.body.appendChild(script);
    };

    const fetchOrder = async () => {
      try {
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/orders/${id}`,
          {
            headers: {
              Authorization: `Bearer ${userInfo.token}`,
            },
          }
        );
        setOrder(data);
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || err.message);
        setLoading(false);
      }
    };

    if (!userInfo) {
      navigate('/login');
    } else {
      if (!order || (order && order._id !== id)) {
        fetchOrder();
      }
      if (!window.paypal && !sdkReady && process.env.REACT_APP_PAYPAL_CLIENT_ID) {
        addPayPalScript();
      } else {
        setSdkReady(true);
      }
    }
  }, [id, userInfo, navigate, order, sdkReady]);

  useEffect(() => {
    if (sdkReady && window.paypal && order && !order.isPaid) {
      const paypalButtons = window.paypal.Buttons({
        createOrder: (data, actions) => {
          return actions.order.create({
            purchase_units: [
              {
                amount: {
                  value: order.totalPrice.toFixed(2),
                },
              },
            ],
          });
        },
        onApprove: (data, actions) => {
          return actions.order.capture().then(function(details) {
            successPaymentHandler({
              id: details.id,
              status: details.status,
              update_time: details.update_time,
              email_address: details.payer.email_address,
            });
          });
        },
        onError: (err) => {
          alert(`Payment error: ${err.message}`);
        },
      });

      if (document.getElementById('paypal-button-container') && !document.getElementById('paypal-button-container').hasChildNodes()) {
        paypalButtons.render('#paypal-button-container');
      }

      return () => {
        if (paypalButtons.close) {
          paypalButtons.close();
        }
      };
    }
  }, [sdkReady, order, successPaymentHandler]);

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger my-5">{error}</div>;

  return (
    <div className="container my-5">
      <h2 className="mb-4">Order #{order._id}</h2>
      <div className="row">
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Shipping</h4>
              <p>
                <strong>Address:</strong> {order.shippingAddress.address}, {order.shippingAddress.city}{' '}
                {order.shippingAddress.postalCode}, {order.shippingAddress.country}
              </p>
            </div>
          </div>
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Payment Method</h4>
              <p>
                <strong>Method:</strong> {order.paymentMethod}
              </p>
              {order.isPaid ? (
                <div className="alert alert-success">
                  Paid on {new Date(order.paidAt).toLocaleDateString()}
                </div>
              ) : (
                <div className="alert alert-warning">Not Paid</div>
              )}
            </div>
          </div>
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Order Items</h4>
              {order.orderItems.map((item) => (
                <div key={item._id} className="d-flex justify-content-between mb-3">
                  <div className="d-flex align-items-center">
                    <img
                      src={item.image}
                      alt={item.name}
                      style={{ width: '50px', height: '50px', objectFit: 'cover', marginRight: '15px' }}
                    />
                    <span>{item.name}</span>
                  </div>
                  <div>
                    {item.qty} x ${item.price} = ${(item.qty * item.price).toFixed(2)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Order Summary</h4>
              <div className="d-flex justify-content-between mb-2">
                <span>Items:</span>
                <span>${order.itemsPrice.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Shipping:</span>
                <span>${order.shippingPrice.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Tax:</span>
                <span>${order.taxPrice.toFixed(2)}</span>
              </div>
              <div className="d-flex justify-content-between fw-bold">
                <span>Total:</span>
                <span>${order.totalPrice.toFixed(2)}</span>
              </div>
              {!order.isPaid && (
                <div className="mt-3">
                  {process.env.REACT_APP_PAYPAL_CLIENT_ID ? (
                    <div id="paypal-button-container"></div>
                  ) : (
                    <div className="alert alert-warning">
                      PayPal integration not configured. Please set REACT_APP_PAYPAL_CLIENT_ID in your .env file.
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <style>{`
  /* Order Confirmation Container */
  .container.my-5 {
    padding: 2rem;
    animation: fadeIn 0.6s ease-out;
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  /* Header Styles */
  h2.mb-4 {
    color: #22c9be;
    font-weight: 700;
    position: relative;
    padding-bottom: 0.5rem;
  }

  h2.mb-4::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 60px;
    height: 3px;
    background: linear-gradient(90deg, #22c9be, #1aa89f);
    border-radius: 3px;
  }

  /* Card Styles */
  .card {
    border: none;
    border-radius: 12px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    transition: all 0.3s cubic-bezier(0.25, 0.8, 0.25, 1);
    margin-bottom: 1.5rem;
    overflow: hidden;
  }

  .card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.12);
  }

  .card-body {
    padding: 1.75rem;
  }

  .card-title {
    color: #22c9be;
    font-weight: 600;
    margin-bottom: 1.5rem;
    position: relative;
    font-size: 1.25rem;
  }

  .card-title::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 0;
    width: 40px;
    height: 2px;
    background: linear-gradient(90deg, #22c9be, #1aa89f);
    border-radius: 2px;
  }

  /* Alert Styles */
  .alert {
    border-radius: 8px;
    padding: 0.75rem 1.25rem;
    margin: 1rem 0;
    animation: fadeIn 0.4s ease-out;
  }

  .alert-success {
    background-color: rgba(34, 201, 190, 0.1);
    border-color: rgba(34, 201, 190, 0.2);
    color: #1aa89f;
  }

  .alert-warning {
    background-color: rgba(255, 193, 7, 0.1);
    border-color: rgba(255, 193, 7, 0.2);
    color: #ffc107;
  }

  /* Order Items */
  .d-flex.justify-content-between.mb-3 {
    padding: 1rem;
    border-radius: 8px;
    transition: all 0.3s ease;
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
  }

  .d-flex.justify-content-between.mb-3:hover {
    background-color: rgba(34, 201, 190, 0.05);
    transform: translateX(5px);
  }

  img {
    border-radius: 6px;
    transition: transform 0.3s ease;
  }

  img:hover {
    transform: scale(1.05);
  }

  /* Order Summary */
  .d-flex.justify-content-between {
    padding: 0.5rem 0;
    border-bottom: 1px dashed rgba(0, 0, 0, 0.1);
  }

  .d-flex.justify-content-between.fw-bold {
    margin-top: 1rem;
    padding-top: 0.75rem;
    border-top: 2px solid rgba(34, 201, 190, 0.3);
    border-bottom: none;
    font-size: 1.1rem;
    color: #333;
  }

  /* PayPal Button Container */
  #paypal-button-container {
    margin-top: 1.5rem;
    border-radius: 8px;
    overflow: hidden;
    transition: all 0.3s ease;
  }

  #paypal-button-container:hover {
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  }

  /* Loading State */
  .text-center.my-5 {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 300px;
    color: #22c9be;
    font-size: 1.25rem;
    font-weight: 500;
  }

  /* Error State */
  .alert-danger {
    background-color: rgba(220, 53, 69, 0.1);
    border-color: rgba(220, 53, 69, 0.2);
    color: #dc3545;
    padding: 1.25rem;
    border-radius: 8px;
    margin: 2rem auto;
    max-width: 800px;
  }

  /* Responsive Adjustments */
  @media (max-width: 768px) {
    .container.my-5 {
      padding: 1rem;
    }
    
    .card-body {
      padding: 1.25rem;
    }
    
    .row {
      flex-direction: column;
    }
    
    .col-md-4 {
      margin-top: 2rem;
    }
  }

  /* Focus States */
  button:focus-visible,
  a:focus-visible {
    outline: 2px solid #1aa89f;
    outline-offset: 2px;
    box-shadow: 0 0 0 4px rgba(34, 201, 190, 0.3);
  }
`}</style>
    </div>
  );
};

export default OrderConfirmation;
