import React, { useState, useContext, useEffect } from 'react';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { AppContext } from '../context/AppContext';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { login } = useContext(AppContext);
  const navigate = useNavigate();
  const location = useLocation();

  const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    return () => {
      setIsSubmitting(false);
    };
  }, []);

  const submitHandler = async (e) => {
    e.preventDefault();
    
    if (isSubmitting) return;
    setIsSubmitting(true);

    // Enhanced validation
    if (!email.trim()) {
      setError('Please enter your email address');
      setIsSubmitting(false);
      return;
    }

    if (!password.trim()) {
      setError('Please enter your password');
      setIsSubmitting(false);
      return;
    }

    // Basic email format validation
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      setError('Please enter a valid email address');
      setIsSubmitting(false);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      await login(email, password);
      navigate(redirect || '/');
    } catch (err) {
      // Handle different types of errors
      if (err.response && err.response.status === 401) {
        setError('Invalid email or password. Please try again.');
      } else if (err.message) {
        setError(err.message);
      } else {
        setError('Login failed. Please try again later.');
      }
    } finally {
      setIsLoading(false);
      setIsSubmitting(false);
    }
  };

  return (
    <div className="login-container" style={styles.container}>
      <div className="login-background" style={styles.background}></div>
      <div className="row justify-content-center" style={styles.row}>
        <div className="col-md-6 col-lg-4">
          <div className="login-card" style={styles.card}>
            <div className="card-body">
              <h2 className="login-title" style={styles.title}>
                Sign In
                <div className="login-spotify-logo" style={styles.spotifyLogo}>
                  <svg viewBox="0 0 24 24" width="24" height="24" fill="#1DB954">
                    <path d="M12 0C5.4 0 0 5.4 0 12s5.4 12 12 12 12-5.4 12-12S18.66 0 12 0zm5.521 17.34c-.24.359-.66.48-1.021.24-2.82-1.74-6.36-2.101-10.561-1.141-.418.122-.779-.179-.899-.539-.12-.421.18-.78.54-.9 4.56-1.021 8.52-.6 11.64 1.32.42.18.479.659.301 1.02zm1.44-3.3c-.301.42-.841.6-1.262.3-3.239-1.98-8.159-2.58-11.939-1.38-.479.12-1.02-.12-1.14-.6-.12-.48.12-1.021.6-1.141C9.6 9.9 15 10.561 18.72 12.84c.361.181.54.78.241 1.2zm.12-3.36C15.24 8.4 8.82 8.16 5.16 9.301c-.6.179-1.2-.181-1.38-.721-.18-.601.18-1.2.72-1.381 4.26-1.26 11.28-1.02 15.721 1.621.539.3.719 1.02.419 1.56-.299.421-1.02.599-1.559.3z"/>
                  </svg>
                </div>
              </h2>
              
              {error && (
                <div className="login-error" style={styles.error}>
                  <i className="bi bi-exclamation-triangle-fill me-2"></i>
                  {error}
                </div>
              )}

              <form onSubmit={submitHandler}>
                <div className="mb-4" style={styles.formGroup}>
                  <label htmlFor="email" className="login-label" style={styles.label}>
                    Email Address
                  </label>
                  <input
                    type="email"
                    className={`login-input ${error?.toLowerCase().includes('email') ? 'is-invalid' : ''}`}
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    autoComplete="email"
                    disabled={isLoading}
                    style={styles.input}
                  />
                </div>

                <div className="mb-4" style={styles.formGroup}>
                  <label htmlFor="password" className="login-label" style={styles.label}>
                    Password
                  </label>
                  <input
                    type="password"
                    className={`login-input ${error?.toLowerCase().includes('password') ? 'is-invalid' : ''}`}
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    autoComplete="current-password"
                    disabled={isLoading}
                    style={styles.input}
                  />
                </div>

                <button 
                  type="submit" 
                  className="login-button"
                  disabled={isLoading || isSubmitting}
                  style={styles.button}
                >
                  {isLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      Signing In...
                    </>
                  ) : 'Sign In'}
                </button>
              </form>

              <div className="login-footer" style={styles.footer}>
                <small style={styles.footerText}>
                  New Customer?{' '}
                  <Link 
                    to={redirect ? `/register?redirect=${redirect}` : '/register'}
                    className="login-link"
                    style={styles.link}
                  >
                    Create an account
                  </Link>
                </small>
                <div className="mt-2">
                  <Link 
                    to="/forgot-password" 
                    className="login-link"
                    style={styles.link}
                  >
                    Forgot password?
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Spotify-inspired theme styles
const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    position: 'relative',
    overflow: 'hidden',
    padding: '2rem',
    background: 'linear-gradient(180deg, #121212 0%, #000000 100%)',
  },
  background: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: 'radial-gradient(circle at 20% 30%, rgba(29, 185, 84, 0.1) 0%, transparent 30%)',
    zIndex: 0,
  },
  row: {
    width: '100%',
    zIndex: 1,
  },
  card: {
    backgroundColor: 'rgba(32, 32, 32, 0.9)',
    borderRadius: '12px',
    padding: '2rem',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
    backdropFilter: 'blur(8px)',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    transition: 'all 0.3s ease',
    ':hover': {
      boxShadow: '0 12px 40px rgba(29, 185, 84, 0.2)',
    },
  },
  title: {
    color: '#ffffff',
    textAlign: 'center',
    marginBottom: '2rem',
    fontSize: '2rem',
    fontWeight: '700',
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  spotifyLogo: {
    marginLeft: '10px',
    animation: 'pulse 2s infinite',
  },
  error: {
    backgroundColor: 'rgba(255, 51, 51, 0.2)',
    color: '#ff6666',
    padding: '0.75rem 1rem',
    borderRadius: '6px',
    marginBottom: '1.5rem',
    borderLeft: '4px solid #ff3333',
    display: 'flex',
    alignItems: 'center',
    animation: 'fadeIn 0.3s ease',
  },
  formGroup: {
    marginBottom: '1.5rem',
  },
  label: {
    display: 'block',
    color: '#b3b3b3',
    marginBottom: '0.5rem',
    fontSize: '0.9rem',
    fontWeight: '600',
    transition: 'all 0.3s ease',
  },
  input: {
    width: '100%',
    padding: '0.75rem 1rem',
    borderRadius: '6px',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    color: '#ffffff',
    fontSize: '0.95rem',
    transition: 'all 0.3s ease',
    ':focus': {
      outline: 'none',
      borderColor: '#1DB954',
      boxShadow: '0 0 0 2px rgba(29, 185, 84, 0.2)',
      backgroundColor: 'rgba(255, 255, 255, 0.08)',
    },
    ':disabled': {
      opacity: 0.7,
      cursor: 'not-allowed',
    },
  },
  button: {
    width: '100%',
    padding: '0.75rem',
    borderRadius: '30px',
    border: 'none',
    backgroundColor: '#1DB954',
    color: '#ffffff',
    fontSize: '1rem',
    fontWeight: '700',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    marginTop: '1rem',
    ':hover': {
      backgroundColor: '#1ed760',
      transform: 'translateY(-2px)',
      boxShadow: '0 4px 15px rgba(29, 185, 84, 0.4)',
    },
    ':disabled': {
      backgroundColor: '#535353',
      cursor: 'not-allowed',
      transform: 'none',
      boxShadow: 'none',
    },
  },
  footer: {
    marginTop: '2rem',
    textAlign: 'center',
  },
  footerText: {
    color: '#b3b3b3',
