import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const AdminPanel = () => {
  const { state } = useContext(AppContext);
  const { userInfo } = state;
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!userInfo || !userInfo.isAdmin) {
      navigate('/login');
      return;
    }

    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/products`,
          {
            headers: {
              Authorization: `Bearer ${userInfo.token}`,
            },
          }
        );

        // Ensure all products are marked as featured
        const featuredProducts = Array.isArray(data) ? data : 
                               (data?.products ? data.products : []);
        
        // Update each product to be featured
        const updatedProducts = featuredProducts.map(product => ({
          ...product,
          isFeatured: true
        }));

        setProducts(updatedProducts);
      } catch (err) {
        const errorMsg = err.response?.data?.message || err.message || 'Failed to fetch products';
        setError(errorMsg);
        alert(`Error: ${errorMsg}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [userInfo, navigate]);

  const markAllFeatured = async () => {
    if (!window.confirm('Are you sure you want to mark ALL products as featured?')) {
      return;
    }

    try {
      // Update all products to be featured in the backend
      await Promise.all(products.map(product => 
        axios.put(
          `${process.env.REACT_APP_API_URL}/api/products/${product._id}`,
          { ...product, isFeatured: true },
          {
            headers: {
              Authorization: `Bearer ${userInfo.token}`,
            },
          }
        )
      ));

      // Update local state
      setProducts(products.map(product => ({
        ...product,
        isFeatured: true
      })));
      
      alert('All products have been marked as featured!');
    } catch (err) {
      const errorMsg = err.response?.data?.message || err.message || 'Failed to update products';
      alert(`Error: ${errorMsg}`);
    }
  };

  const deleteHandler = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) {
      return;
    }

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/products/${id}`,
        {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      setProducts(prevProducts => prevProducts.filter(product => product._id !== id));
      alert('Product deleted successfully');
    } catch (err) {
      const errorMsg = err.response?.data?.message || err.message || 'Failed to delete product';
      alert(`Error: ${errorMsg}`);
      setError(errorMsg);
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center my-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger my-4">
        Error: {error}
        <button 
          className="btn btn-sm btn-outline-danger ms-3"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Admin Panel - All Products Featured</h2>
        <div>
          <button 
            className="btn btn-success me-2"
            onClick={markAllFeatured}
          >
            Mark All Featured
          </button>
          <Link to="/admin/product/create" className="btn btn-primary me-2">
            Create Product
          </Link>
          <button 
            className="btn btn-outline-secondary"
            onClick={() => window.location.reload()}
          >
            Refresh
          </button>
        </div>
      </div>

      {products.length === 0 ? (
        <div className="alert alert-info">
          No products found. Create your first product.
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price</th>
                <th>Stock</th>
                <th>Featured</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product._id}>
                  <td>{product._id.substring(0, 8)}...</td>
                  <td>
                    <Link to={`/product/${product._id}`} className="text-decoration-none">
                      {product.name}
                    </Link>
                  </td>
                  <td>${product.price.toFixed(2)}</td>
                  <td className={product.stock <= 0 ? 'text-danger' : ''}>
                    {product.stock}
                  </td>
                  <td>
                    <span className="badge bg-success">Yes</span>
                  </td>
                  <td>
                    <div className="d-flex gap-2">
                      <Link
                        to={`/admin/product/${product._id}/edit`}
                        className="btn btn-sm btn-warning"
                      >
                        Edit
                      </Link>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => deleteHandler(product._id)}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
